// Realistic Skate - Medium complexity
// Overall length ~85 mm, suitable for 3D printing

$fn = 36;  // Smooth curves

// Main assembly
module skate() {
    union() {
        blade_assembly();
        boot();
        laces();
    }
}

// Blade and holder
module blade_assembly() {
    translate([0, 0, 6]) {
        // Blade holder - left side
        translate([-4, 0, 0])
            blade_holder();

        // Blade holder - right side (mirrored)
        translate([4, 0, 0])
            mirror([1, 0, 0])
                blade_holder();

        // Metal blade
        translate([0, 0, -2])
            blade();
    }
}

module blade_holder() {
    // Main holder body
    difference() {
        union() {
            // Front post
            translate([0, 35, 0])
                cylinder(h = 12, d1 = 6, d2 = 4);

            // Rear post
            translate([0, -35, 0])
                cylinder(h = 12, d1 = 6, d2 = 4);

            // Connecting bridge
            hull() {
                translate([0, 35, 8])
                    sphere(d = 4);
                translate([0, -35, 8])
                    sphere(d = 4);
            }
        }

        // Hollow out for weight reduction
        for (i = [-1, 1]) {
            translate([i * 1.5, 20, 6])
                cylinder(h = 10, d = 2, $fn = 6);
            translate([i * 1.5, -20, 6])
                cylinder(h = 10, d = 2, $fn = 6);
        }
    }
}

module blade() {
    // Tapered blade profile
    linear_extrude(height = 1.5, scale = [0.9, 1])
        polygon(points = [
            [-2, 42],
            [2, 42],
            [1.5, 0],
            [-1.5, 0],
            [-2, -42],
            [2, -42]
