// Skipper (butterfly larva) – OpenSCAD, realistic medium-complexity
// Overall length ≈ 80 mm, printable without support

// Basic parameters
body_d = 12;            // body diameter
body_l = 60;            // body length
head_d = 10;            // head diameter
neck_d = 6;             // neck diameter
leg_l = 8;              // proleg length
leg_d = 2.2;            // proleg diameter
wall_t = 1.2;           // wall thickness for hollow parts
seg_cnt = 8;            // body segments
$fn = 36;

// Colors for preview
color_body = "#2E7D32";
color_head = "#4CAF50";

module skipper() {
    color(color_body) {
        union() {
            // Head
            translate([body_l/2 + head_d/2, 0, 0])
                rotate([0, 90, 0])
                    cylinder(d1 = head_d, d2 = head_d*0.9, h = head_d*1.1);

            // Neck
            translate([body_l/2 - 2, 0, 0])
                rotate([0, 90, 0])
                    cylinder(d = neck_d, h = 6);

            // Body segments
            for (i = [0:seg_cnt-1]) {
                seg_l = body_l / seg_cnt;
                seg_pos = -body_l/2 + i*seg_l + seg_l/2;
                factor = 1 - 0.1*i;
                hull() {
                    translate([seg_pos - seg_l*0.3, 0, 0])
                        sphere(d = body_d * factor);
                    translate([seg_pos + seg_l*0.3, 0, 0])
                        sphere(d = body_d * factor * 0.9);
                }
            }

            // Prolegs (true legs under thorax + prolegs on abdomen)
            for (pos = [-body_l/2 + 6, -body_l/2 + 18, -body_l/2 + 34, -body_l/2 + 46]) {
                for (side = [-1, 1]) {
                    translate([pos, side*body_d/2 - 0.5, -leg_l/2])
                        rotate([90, 0, side*10])
                            cylinder(d = leg_d, h = leg_l);
                }
            }

            // Spiracles
            for (i = [1:3]) {
                spir_pos = -body_l/2 + 15*i;
                translate([spir_pos, body_d/2 - 0.5, 0])
                    sphere(d = 1.5);
                translate([spir_pos, -body_d/2 + 0.5, 0])
                    sphere(d = 1.5);
            }

            // Tail claspers
            translate([-body_l/2 - 4, 0, 0])
                rotate([0, 90, 0])
                    cylinder(d1 = 6, d2 = 4, h = 8);
        }
    }

    // Simple mandibles
    color(color_head) {
        translate([body_l/2 + head_d/2 + 2, 3, 0])
            rotate([0, 90, 0])
                cylinder(d = 2.5, h = 3);
        translate([body_l/2 + head_d/2 + 2, -3, 0])
            rotate([0, 90, 0])
                cylinder(d = 2.5, h = 3);
    }
}

// Hollow body for filament saving
difference() {
    skipper();
    // Inner cavity
    translate([0, 0, -2])
        scale([0.8, 0.8, 0.8])
            skipper();
}

// Lay flat on bed
rotate([0, -90, 0])
    translate([0, 0, body_d/2])
        children();

