            scale([0.6, 0.15, 0.05])
            sphere(d=wing_span/3);

        // Wing feathers
        for(i = [0:3]) {
            translate([5 + i*3, wing_span/4 - 8 + i*2, body_height/2 + 1.5])
                rotate([0, 0, 45 - i*10])
                scale([0.8, 0.1, 0.03])
                cube([5, 2, 0.5]);
