// Realistic-style Pool Skimmer – OpenSCAD
// Medium complexity, 3D-print friendly, ~90 mm long

$fn = 32;

// Overall size
skimmer_length = 90;
skimmer_width  = 38;
skimmer_height = 30;
wall_thick     = 2.2;

module skimmer_body() {
    // Main hollow scoop
    difference() {
        // Outer rounded box
        minkowski() {
            cube([skimmer_length, skimmer_width, skimmer_height], center = true);
            sphere(3);
        }
        // Inner cavity
        translate([0,0,wall_thick/2])
        minkowski() {
            cube([
                skimmer_length - 2*wall_thick,
                skimmer_width  - 2*wall_thick,
                skimmer_height - 1.5*wall_thick
            ], center = true);
            sphere(2.5);
        }
    }

    // Flatten bottom for stable print
    translate([0,0,-skimmer_height/2 - 3])
    cube([skimmer_length + 6, skimmer_width + 6, 6], center = true);
}

module curved_handle() {
    handle_len = 55;
    handle_d   = 8;
    // Simple curved cylinder
    for (i = [0:10]) {
        rotate([0, i*3, 0])
        translate([skimmer_length/2 - 5 + i*4.5, 0, 0])
        rotate([90,0,0])
        cylinder(h = 1.5, d = handle_d, center = true);
    }
    // End cap
    translate([skimmer_length/2 + handle_len - 5, 0, 0])
    rotate([90,0,0])
    cylinder(h = handle_d, d = handle_d, center = true);
}

module fine_mesh() {
    // Thin frame inside mouth
    thickness = 1.8;
    translate([-skimmer_length/2 + thickness/2, 0, 0])
    cube([thickness, skimmer_width - 2*wall_thick, skimmer_height - 2*wall_thick], center = true);

    // Simple grid of bars
    bar_width = 2;
    bar_gap   = 4;
    num_bars  = floor((skimmer_height - 2*wall_thick) / (bar_width + bar_gap));
    for (i = [-num_bars/2 : num_bars/2]) {
        translate([-skimmer_length/2 + thickness/2,
                   0,
                   i*(bar_width + bar_gap)])
        cube([thickness, skimmer_width - 2*wall_thick - 4, bar_width], center = true);
    }
}

module mounting_hook() {
    hook_w = 12;
    hook_h = 9;
    translate([skimmer_length/2 + 5, 0, skimmer_height/2 - 2])
    rotate([0,90,0])
    difference() {
        cylinder(h = hook_w, d = hook_h, center = true);
        translate([0,0,-hook_w/2 - 1])
        cube([hook_h + 2, hook_h + 2, hook_w + 2], center = true);
    }
}

// Assembly
union() {
    skimmer_body();
    curved_handle();
    fine_mesh();
    mounting_hook();
}

