skunk_width = 30;

module skunk_body() {
    // Main torso - elongated ellipsoid
    scale([1.5, 1, 0.8])
        sphere(d = skunk_width);

    // Rump extension
    translate([-skunk_length/2 + 5, 0, -5])
        scale([1.2, 1, 0.6])
            sphere(d = skunk_width);
}

module head() {
    // Skull shape
    translate([skunk_length/2 - 10, 0, skunk_height/2 - 5])
        rotate([0, -10, 0])
        scale([0.9, 1, 1.1])
            sphere(d = 20);

    // Snout extension
    translate([skunk_length/2 + 5, 0, skunk_height/2 - 8])
        rotate([0, -20, 0])
        scale([1.5, 0.8, 0.7])
            sphere(d = 12);
}

module stripe_pattern() {
    // White dorsal stripe
    for (i = [-20:5:20]) {
        translate([i, 0, skunk_height/2 - 2])
            rotate([0, 90, 0])
                cylinder(h = 10, d1 = 8, d2 = 6, center = true);
    }
}

module legs() {
    // Front legs (4 legs total)
    for (side = [-1, 1]) {
        // Front
        translate([skunk_length/2 - 15, side * (skunk_width/2 - 5), -skunk_height/2])
            rotate([10, 0, 0])
                cylinder(h = 15, d1 = 6, d2 = 4);

        // Back
        translate([-skunk_length/2 + 10, side * (skunk_width/2 - 5), -skunk_height/2])
            rotate([10, 0, 0])
                cylinder(h = 15, d1 = 6, d2 = 4);
    }
}

module tail() {
    // Bushy tail - curved cylinder segments
    for (i = [0:15:75]) {
        rotate([0, i/2, 0])
            translate([-skunk_length/2 - 15 + i/2, 0, -5 + sin(i)*5])
                rotate([90, 0, 0])
                    cylinder(h = 12, d1 = 12 - i/10, d2 = 10 - i/10);
    }
}

module ears() {
    // Triangular ears
    for (side = [-1, 1]) {
        translate([skunk_length/2 - 5, side * 8, skunk_height/2 + 5])
            rotate([0, -20, side * 30])
                scale([1, 0.5, 1.5])
                    cylinder(h = 8, d1 = 8, d2 = 0);
    }
}

module eyes() {
    // Eyes as spheres (printable as indentations)
    for (side = [-1, 1]) {
        translate([skunk_length/2 + 2, side * 6, skunk_height/2 - 3])
            sphere(d = 3);
    }
}

module nose() {
    // Triangular nose
    translate([skunk_length/2 + 8, 0, skunk_height/2 - 6])
        rotate([90, 0, 0])
            cylinder(h = 4, d1 = 5, d2 = 0);
}

// Assemble the skunk
union() {
    // Main body parts
    skunk_body();
    head();
    tail();

    // Appendages
    legs();
    ears();

    // Features
    eyes();
    nose();

    // Stripe pattern (subtractive)
    difference() {
        children();
        color("white") stripe_pattern();
    }
}

