// Realistic Medium-Complexity Siamese Cat
// Overall height ~70 mm, sitting pose, optimized for FDM

// Parameters
body_len = 40;
body_wid = 25;
body_h   = 35;
head_r   = 15;
ear_len  = 12;
ear_wid  = 7;
tail_len = 50;
tail_rad = 5;
leg_len  = 18;
leg_rad  = 5;
paw_rad  = 6;

// Colors for preview
$fa = 1;
$fs = 0.4;

// Main assembly
union() {
    body();
    head();
    tail();
    legs();
}

module body() {
    // Core torso
    difference() {
        scale([1, 0.9, 1])
            sphere(d = body_len);
        // Undercut for sitting
        translate([0, 0, -body_h/2])
            cube([body_len*2, body_wid*2, body_h], center = true);
    }

    // Chest bulge
    translate([body_len/2-5, 0, -5])
        sphere(d = 20);
}

module head() {
    // Skull
    translate([body_len/2+head_r-5, 0, 10])
        sphere(d = head_r*2);

    // Snout
    translate([body_len/2+head_r+5, 0, 5])
        scale([1.3,0.9,0.7])
            sphere(d = 12);

    // Ears
    for(side = [-1, 1])
        translate([body_len/2+head_r-3, side*10, 20])
            rotate([0, 0, side*10])
                ear();

    // Eyes
    for(side = [-1, 1])
        translate([body_len/2+head_r+8, side*5, 10])
            sphere(d = 3);

    // Nose
    translate([body_len/2+head_r+12, 0, 7])
        sphere(d = 2.5);
}

module ear() {
    difference() {
        // Outer ear cone
        cylinder(h = ear_len, r1 = ear_wid, r2 = 2, center = false);
        // Inner ear cavity
        translate([0,0,2])
            cylinder(h = ear_len-2, r1 = ear_wid-3, r2 = 1, center = false);
    }
}

module tail() {
    // S-curved tail resting on build plate
    for(i = [0:5:tail_len]) {
        hull() {
            translate([sin(i*4)*10 - 15, cos(i*4)*10, i*0.8])
                sphere(r = tail_rad*(1-i/tail_len*0.3));
            translate([sin((i+5)*4)*10 - 15, cos((i+5)*4)*10, (i+5)*0.8])
                sphere(r = tail_rad*(1-(i+5)/tail_len*0.3));
        }
    }
}

module legs() {
    // Front legs
    for(side = [-1, 1])
        translate([body_len/4, side*(body_wid/2+leg_rad-2), -body_h/2+2])
            rotate([90, 0, 0])
                union() {
                    cylinder(h = leg_len, r = leg_rad);
                    translate([0,0,leg_len])
                        sphere(r = paw_rad);
                }

    // Rear legs tucked under
    for(side = [-1, 1])
        translate([-body_len/4, side*(body_wid/2-5), -body_h/2+2])
            rotate([100, 0, side*30])
                union() {
                    cylinder(h = leg_len*0.8, r = leg_rad*0.9);
                    translate([0,0,leg_len*0.8])
                        sphere(r = paw_rad*0.9);
                }
}

