// Realistic Snapper (medium complexity)
// Overall length ~75 mm, printable without support

$fn=50;

module shell_segment(id, length, height, width) {
    // Create one segment of the curved shell
    hull() {
        translate([0, 0, 0]) 
            scale([1, 0.9, 0.4]) sphere(d=width);
        translate([length*0.8, 0, height*0.7]) 
            scale([0.7, 0.7, 0.3]) sphere(d=width);
    }
}

module shell() {
    // Main curved shell made of overlapping segments
    union() {
        for (i=[0:6]) {
            translate([-i*8, 0, -i*3])
                rotate([0, -i*5, 0])
                shell_segment(i, 15, 12-i, 22-i*1.5);
        }
    }
}

module head() {
    // Realistic triangular head with eye ridges
    difference() {
        // Main head block
        translate([-35, 0, 5])
            rotate([0, 10, 0])
            scale([1.3, 1, 0.8])
            sphere(d=20);

        // Mouth cavity
        translate([-45, 0, -5])
            rotate([0, 30, 0])
            cube([20, 15, 8], center=true);

        // Eye sockets
        translate([-30, 8, 8])
            sphere(d=6);
        translate([-30, -8, 8])
            sphere(d=6);
    }

    // Beak tip
    translate([-48, 0, 0])
        rotate([0, -20, 0])
        cylinder(h=10, r1=3, r2=0, center=true);
}

module eye() {
    // Eyeball with pupil
    color("black")
    translate([-30, 6, 8])
        sphere(d=4);
    translate([-30, -6, 8])
        sphere(d=4);
}

