// Realistic 3D Printable Sparrow - Medium Complexity
// Overall dimensions: ~80mm long, ~40mm tall

module sparrow() {
    // Main body
    difference() {
        union() {
            // Torso - ellipsoid
            scale([1.2, 0.8, 1]) sphere(r=12, $fn=32);

            // Rounded chest
            translate([0, -5, -3]) 
                scale([1.3, 1, 0.7]) sphere(r=10, $fn=32);
        }

        // Remove bottom flat for printing
        translate([0, 0, -15]) cube([40, 40, 15], center=true);
    }

    // Head - sphere with beak
    translate([-15, 0, 8]) {
        sphere(r=8, $fn=32);

        // Beak - cone
        rotate([0, -20, 0]) 
            translate([-4, 0, 0]) 
                cylinder(h=12, r1=2.5, r2=0.5, $fn=8);
    }

    // Eyes - small spheres
    for(side = [-1, 1]) {
        translate([-18, side*4, 9]) 
            sphere(r=1.2, $fn=8);
    }

    // Wings - layered feathers
    module wing(is_left = true) {
        mirror = is_left ? 1 : -1;

        translate([0, mirror*12, 5]) {
            rotate([0, 0, -20*mirror]) {
                // Primary wing shape
                difference() {
                    scale([1.8, 0.3, 0.8]) sphere(r=10, $fn=16);

                    // Cut bottom
                    translate([0, 0, -10]) cube([40, 10, 10], center=true);

                    // Feather separation cuts
                    for(i = [1:5]) {
                        rotate([0, 0, i*8]) 
                            translate([i*3-5, -2, -5]) 
                                cube([0.5, 4, 10]);
                    }
                }

                // Secondary feathers
                for(i = [0:4]) {
                    translate([i*3-6, 0, -3+i]) 
                        rotate([0, 0, 5-i]) 
                            scale([1.5, 0.1, 0.3]) 
                                sphere(r=5-i*0.5, $fn=8);
                }
            }
        }
    }

    wing(true);
    wing(false);

    // Tail - fan of feathers
    module tail_feather(index) {
        rotate([0, 0, index*10-25]) {
            translate([15+index*0.5, 0, -2+abs(index)*0.5]) {
                scale([0.8, 0.1, 0.3]) 
                    cylinder(h=15-abs(index)*2, r1=3, r2=1, $fn=8);
            }
        }
    }

    for(i = [-3:3]) {
        tail_feather(i);
    }

    // Legs and feet
    module leg(side) {
        translate([-5, side*4, -10]) {
            // Leg
            cylinder(h=8, r=1.5, $fn=8);

            // Foot
            translate([0, 0, -8]) {
                // Toes
                for(rot = [-30, 0, 30]) {
                    rotate([0, 0, rot]) {
                        translate([0, 0, 0]) 
                            cylinder(h=6, r1=1, r2=0.5, $fn=6);
                    }
                }
            }
        }
    }

    leg(1);
    leg(-1);

    // Chest detail - feather texture
    for(i = [0:5]) {
        for(j = [0:2]) {
            translate([i*2-8, j*2-4, -2]) {
                rotate([90, 0, 0]) 
                    scale([0.3, 0.1, 0.2]) 
                        cylinder(h=2, r=3, $fn=6);
            }
        }
    }
}

// Main assembly
sparrow();

