// Snowy realistic model - OpenSCAD
// 3D printable snowman with realistic proportions

$fn = 32;  // Smooth curves for printing

// Overall scale factor
scale_factor = 0.4;

// Dimensions (mm)
base_radius = 30 * scale_factor;
mid_radius = 22 * scale_factor;
head_radius = 17 * scale_factor;
arm_length = 25 * scale_factor;
arm_diameter = 3 * scale_factor;

// Helper module for spheres with wall thickness
module hollow_sphere(r, wall) {
    difference() {
        sphere(r);
        sphere(r - wall);
    }
}

// Snowball with realistic rounding
module snowball(r, height_ratio = 1) {
    scale([1, 1, height_ratio]) {
        difference() {
            sphere(r);
            // Flatten bottom slightly
            translate([0, 0, -r-1])
                cube([r*2.2, r*2.2, 2], center=true);
        }
    }
}

// Branch arm with natural curve
module branch_arm(length, diameter) {
    rotate([0, 15, 0]) {
        // Main branch
        cylinder(h=length, d1=diameter*1.2, d2=diameter*0.8, center=false);

        // Side twigs
        translate([0, diameter/2, length*0.3])
            rotate([0, 90, 45])
                cylinder(h=length*0.4, d1=diameter*0.8, d2=diameter*0.5);

        translate([0, -diameter/2, length*0.5])
            rotate([0, 90, -30])
                cylinder(h=length*0.35, d1=diameter*0.7, d2=diameter*0.4);
    }
}

// Carrot nose
module carrot_nose(length, base_d) {
    cylinder(h=length, d1=base_d, d2=base_d*0.3);
}

// Coal eye
module coal_eye(size) {
    sphere(size);
}

// Button
module button(diameter, thickness) {
    cylinder(h=thickness, d=diameter);
    translate([0, 0, thickness/2])
        cylinder(h=thickness*0.5, d=diameter*0.6);
}

// Scarf segment
module scarf(width, thickness, length) {
    // Main scarf body
    cube([length, width, thickness], center=true);

    // Fringe ends
    for(i = [0:3]) {
        translate([length/2 - 2 - i*4, width/2 + 2, 0])
            cube([0.5, 4, thickness*0.8]);
        translate([length/2 - 2 - i*4, -width/2 - 2, 0])
            cube([0.5, 4, thickness*0.8]);
    }
}

// Main assembly
module snowy() {
    // Base snowball (slightly compressed)
    translate([0, 0, base_radius*0.8])
        snowball(base_radius, 0.8);

    // Middle snowball
    translate([0, 0, base_radius*0.8*2 + mid_radius*0.8])
        snowball(mid_radius, 0.85);

    // Head
    translate([0, 0, base_radius*0.8*2 + mid_radius*0.8*2 + head_radius*0.9])
        snowball(head_radius, 0.9);

    // Left arm
    translate([-mid_radius*0.8, 0, base_radius*0.8*2 + mid_radius*0.8])
        rotate([0, 0, 180])
            branch_arm(arm_length, arm_diameter);

    // Right arm
    translate([mid_radius*0.8, 0, base_radius*0.8*2 + mid_radius*0.8])
        rotate([0, 0, 0])
            branch_arm(arm_length, arm_diameter);

    // Carrot nose
    translate([0, head_radius*0.9, base_radius*0.8*2 + mid_radius*0.8*2 + head_radius*0.9*1.8])
        rotate([90, 0, 0])
            carrot_nose(head_radius*0.8, 3);

    // Eyes
    translate([-head_radius*0.3, head_radius*0.7, base_radius*0.8*2 + mid_radius*0.8*2 + head_radius*0.9*1.7])
        coal_eye(2.5);
    translate([head_radius*0.3, head_radius*0.7, base_radius*0.8*2 + mid_radius*0.8*2 + head_radius*0.9*1.7])
        coal_eye(2.5);

