// Springtail (Collembola) - Realistic style with medium complexity

// Main dimensions (mm)
body_length = 45;
body_height = 12;
head_diameter = 8;
abdomen_width = 10;
furcula_length = 25;
antenna_length = 12;

// Wall thickness for 3D printing
wall = 1.5;

module head() {
    // Main head sphere
    translate([body_length/2 - head_diameter/2, 0, body_height/2])
        sphere(d = head_diameter, $fn=32);

    // Eyes (compound eyes as small spheres)
    for(side = [-1, 1]) {
        translate([body_length/2 - head_diameter/2 + 2, side * 3.5, body_height/2 + 1])
            sphere(d = 2.5, $fn=16);
    }

    // Mouthparts (labium)
    translate([body_length/2 - head_diameter/2 - 2, 0, body_height/2 - 2])
        cube([4, 3, 2], center=true);
}

module antenna() {
    // Antenna segments
    for(side = [-1, 1]) {
        translate([body_length/2 - head_diameter/2 - 1, side * 3, body_height/2 + 3])
            rotate([0, 90, 0])
                for(i = [0:4]) {
                    translate([i * 2, 0, 0])
                        sphere(d = 1.5 - i * 0.2, $fn=8);
                }
    }
}

module thorax() {
    // Three thoracic segments
    for(i = [0, 1, 2]) {
        translate([body_length/2 - 8 - i * 6, 0, body_height/2])
            scale([1, 1 - i * 0.1, 0.8])
                sphere(d = 8, $fn=24);
    }
}

module abdomen() {
    // Main abdomen (fusiform shape)
    translate([0, 0, body_height/2])
        scale([1.2, 1, 0.6])
            sphere(d = abdomen_width, $fn=32);

    // Segmentation rings
    for(i = [1:5]) {
        translate([-i * 2, 0, body_height/2])
            difference() {
                cylinder(h = 1, d1 = abdomen_width - i * 0.8, d2 = abdomen_width - i * 0.8, $fn=24);
                translate([0, 0, -0.1])
                    cylinder(h = 1.2, d1 = (abdomen_width - i * 0.8) - wall*2, d2 = (abdomen_width - i * 0.8) - wall*2, $fn=24);
            }
    }
}

module legs() {
    // Six legs (3 pairs)
    for(pair = [0, 1, 2]) {
        for(side = [-1, 1]) {
            // Coxa
            translate([body_length/2 - 8 - pair * 6, side * 4, body_height/2 - 2])
                rotate([0, 0, side * 45])
                    cylinder(h = 3, d = 1.5, $fn=8);

            // Femur
            translate([body_length/2 - 8 - pair * 6 - 1.5, side * 5.5, body_height/2 - 2])
                rotate([0, 0, side * 60])
                    cylinder(h = 4, d1 = 1.5, d2 = 1, $fn=8);

            // Tibiotarsus
            translate([body_length/2 - 8 - pair * 6 - 4, side * 7, body_height/2 - 2.5])
                rotate([10, 0, side * 70])
                    cylinder(h = 5, d = 0.8, $fn=8);
        }
    }
}

module collophore() {
    // Ventral tube for water absorption
    translate([body_length/2 - 15, 0, 0])
        cylinder(h = 6, d1 = 3, d2 = 2, $fn=16);
}

module furcula() {
    // Spring mechanism (furcula)
    translate([-5, 0, 1])
        rotate([0, 90, 0])
            for(side = [-1, 1]) {
                translate([0, side * 2, 0])
                    difference() {
                        // Main furcula arm
                        rotate([0, side * 10, 0])
                            cylinder(h = furcula_length, d = 2, $fn=12);

