// Realistic Stilt Model - Medium Complexity
// Designed for 3D printing with no overhangs

// Parameters
foot_length = 45;
foot_width = 20;
foot_height = 15;
pole_diameter = 8;
pole_height = 80;
base_diameter = 35;
base_height = 15;
strap_width = 8;
strap_thickness = 2;

module foot() {
    // Main foot platform
    difference() {
        translate([0, 0, foot_height/2]) {
            cube([foot_length, foot_width, foot_height], center=true);
        }

        // Heel cutout for comfort
        translate([-foot_length/2 + foot_length/4, 0, foot_height - 2]) {
            cube([foot_length/2, foot_width - 6, foot_height], center=true);
        }

        // Toe grooves
        for(i = [1:3]) {
            translate([foot_length/2 - i*5, 0, foot_height/2]) {
                cube([2, foot_width - 4, foot_height], center=true);
            }
        }
    }

    // Side supports
    translate([-foot_length/2 + 5, foot_width/2 + 1, foot_height/2]) {
        cube([10, 2, foot_height], center=true);
    }
    mirror([0,1,0]) {
        translate([-foot_length/2 + 5, foot_width/2 + 1, foot_height/2]) {
            cube([10, 2, foot_height], center=true);
        }
    }
}

module pole_connection() {
    // Pole mount cylinder
    cylinder(h=base_height, d=base_diameter, $fn=32);

    // Reinforcement ribs
    for(i = [0:3]) {
        rotate([0, 0, i*90]) {
            translate([base_diameter/2 - 3, 0, base_height/2]) {
                cube([6, 3, base_height], center=true);
            }
        }
    }

    // Pole hole
    translate([0, 0, -0.1]) {
        cylinder(h=base_height + 0.2, d=pole_diameter + 0.4, $fn=32);
    }
}

module pole() {
    // Main pole
    cylinder(h=pole_height, d=pole_diameter, $fn=32);

    // Tapered tip
    translate([0, 0, pole_height]) {
        cylinder(h=5, d1=pole_diameter, d2=pole_diameter*0.7, $fn=32);
    }

    // Rubber tip
    translate([0, 0, pole_height + 5]) {
        cylinder(h=3, d=pole_diameter*0.8, $fn=32);
    }
}

module straps() {
    // Ankle strap
    translate([0, 0, foot_height + 3]) {
        for(i = [0:1]) {
            rotate([0, 0, i*180]) {
                translate([0, foot_width/2 + strap_width/2, 0]) {
                    cube([foot_length - 10, strap_width, strap_thickness], center=true);
                }
            }
        }
    }

    // Heel strap
    translate([-foot_length/4, 0, foot_height + 3]) {
        rotate([90, 0, 0]) {
            cylinder(h=foot_width + strap_width, d=strap_thickness*2, $fn=16);
        }
    }
}

// Assembly
union() {
    // Foot platform
    foot();

    // Pole connection at center
    translate([0, 0, foot_height]) {
        pole_connection();
    }

    // Pole
    translate([0, 0, foot_height + base_height]) {
        pole();
    }

    // Straps
    straps();
}

