    // Central body disc
    difference() {
        scale([1, 0.7, 0.2]) 
            sphere(d=body_width, $fn=50);

        // Flatten the bottom
        translate([0, 0, -body_width*0.1])
            cube([body_width*2, body_width*2, body_width*0.2], center=true);
    }

    // Tail base
    translate([0, -body_length/2, -thickness/4])
        scale([0.3, 1, 0.3])
            cylinder(h=body_length/3, d=body_width/3, $fn=20);
}

// Tail module with stinger
module tail() {
    // Main tail
    translate([0, -body_length/2 - 15, -thickness/2])
        rotate([0, 15, 0])
            scale([0.15, 1, 0.15])
                cylinder(h=body_length/2, d=body_width/3, $fn=8);

    // Stinger tip
    translate([0, -body_length/2 - 35, -thickness/2])
        rotate([0, 15, 0])
            cone(h=8, d1=2, d2=0.1, $fn=6);
}

// Eyes module
module eyes() {
    eye_pos = body_width/4;
    eye_size = 2;

    // Left eye
    translate([eye_pos, body_length/4, thickness/2])
        sphere(d=eye_size, $fn=12);

    // Right eye
    translate([-eye_pos, body_length/4, thickness/2])
        sphere(d=eye_size, $fn=12);
}

// Gill slits
module gills() {
    for(i=[1:5]) {
        translate([0, body_length/4 - i*3, -thickness/4])
            rotate([0, 90, 0])
                cylinder(h=0.5, d=1.5, $fn=6);
    }
}

