    scale([1.5, 1, 1])
    sphere(r = 8);

    // Front legs - cylinders
    for(x = [-15, -5]) {
        translate([x, 10, 0])
        rotate([90, 0, 0])
        cylinder(h = 12, r = 4);
        translate([x, -10, 0])
        rotate([90, 0, 0])
        cylinder(h = 12, r = 4);
    }

    // Hind legs - longer cylinders with paws
    for(x = [15, 25]) {
        translate([x, 8, 0])
        rotate([90, 0, 0])
        cylinder(h = 15, r = 5);
        translate([x, -8, 0])
        rotate([90, 0, 0])
        cylinder(h = 15, r = 5);
    }

    // Tail - curved cylinder segments
    for(i = [0:15:60]) {
        rotate([0, 0, i])
        translate([30 + i/2, 0, 10 + sin(i) * 5])
        rotate([0, -i/8, 0])
        cylinder(h = 8, r1 = 6 - i/20, r2 = 5 - i/20);
    }

    // Eyes - small spheres
    translate([-33, 4, 17])
    sphere(r = 2);
    translate([-33, -4, 17])
    sphere(r = 2);

    // Nose - tiny sphere
    translate([-42, 0, 15])
    sphere(r = 1.5);

    // Whiskers - thin cylinders
    for(angle = [-30, 0, 30]) {
        translate([-38, 0, 15])
        rotate([0, angle, 0])
        cylinder(h = 8, r = 0.5);
    }

    // Body fur texture - small bumps
    for(i = [0:10]) {
        for(j = [0:8]) {
            theta = i * 36;
            phi = j * 45;
            rx = cos(theta) * sin(phi) * 12;
            ry = sin(theta) * sin(phi) * 8;
            rz = cos(phi) * 6 + 10;
            translate([rx, ry, rz])
            sphere(r = 1);
        }
    }
}

