// Realistic Stork - Medium Complexity
// Designed for 3D printing, no overhangs

// Main parameters
body_length = 30;
body_height = 25;
neck_length = 20;
head_size = 12;
leg_length = 35;
wing_span = 45;

module stork_body() {
    // Main body - ellipsoid shape using spheres
    union() {
        // Upper body
        translate([0, 0, body_height/2])
            scale([1.2, 0.8, 1])
            sphere(d=body_length, $fn=32);

        // Lower body/belly
        translate([0, 0, body_height/3])
            scale([1.1, 1, 0.7])
            sphere(d=body_length*0.9, $fn=32);
    }
}

module stork_neck() {
    // Curved neck using cylinders
    union() {
        // Neck segment 1
        translate([-body_length/3, 0, body_height*0.8])
            rotate([0, 45, 0])
            cylinder(h=neck_length/2, d1=8, d2=6, $fn=16);

        // Neck segment 2
        translate([-body_length/3 - neck_length/3, 0, body_height*0.8 + neck_length/3])
            rotate([0, 30, 0])
            cylinder(h=neck_length/2, d1=6, d2=4, $fn=16);
    }
}

module stork_head() {
    // Head with long beak
    union() {
        // Head sphere
        translate([-body_length/2 - neck_length, 0, body_height + neck_length/2])
            sphere(d=head_size, $fn=32);

        // Beak
        translate([-body_length/2 - neck_length - head_size/2, 0, body_height + neck_length/2])
            rotate([0, -20, 0])
            cylinder(h=head_size*1.5, d1=2, d2=4, $fn=16);

        // Beak tip
        translate([-body_length/2 - neck_length - head_size*1.2, 0, body_height + neck_length/2 - 2])
            sphere(d=3, $fn=16);
    }
}

module stork_legs() {
    // Two legs with feet
    for(side = [-1, 1]) {
        // Upper leg
        translate([body_length/4, side*5, 0])
            rotate([0, side*5, 0])
            cylinder(h=leg_length/2, d=3, $fn=16);

        // Lower leg
        translate([body_length/4 + 5, side*5, -leg_length/2])
            rotate([0, side*5, 0])
            cylinder(h=leg_length/2, d=2.5, $fn=16);

        // Foot
        translate([body_length/4 + 10, side*7, -leg_length + 2])
            union() {
                // Main foot
                cube([8, 3, 1]);
                // Toes
                for(i = [0:2]) {
                    translate([i*2.5, side*2, 0])
                        cube([2, 2, 1]);
                }
            }
    }
}

module stork_wings() {
    // Folded wings against body
    for(side = [-1, 1]) {
        // Main wing
        translate([0, side*body_length/3, body_height*0.7])
            rotate([0, 0, side*20])
            scale([1.2, 0.2, 0.8])
            sphere(d=wing_span/2, $fn=32);

        // Wing tip
        translate([wing_span/4, side*body_length/3 + side*5, body_height*0.8])
            rotate([0, 0, side*30])
            scale([0.8, 0.15, 0.6])
            sphere(d=wing_span/3, $fn=32);
    }
}

module stork_tail() {
    // Fan tail
    translate([body_length/2, 0, body_height/3])
        rotate([0, -20, 0])
        scale([0.8, 1, 0.3])
        sphere(d=body_length/3, $fn=32);
}

// Assemble the stork
union() {
    stork_body();
    stork_neck();
    stork_head();
    stork_legs();
    stork_wings();
    stork_tail();
}

