// Swamphen (Porphyrio porphyrio) - Realistic Style, Medium Complexity
// Basic dimensions (mm)
body_length = 60;
body_height = 35;
head_size = 12;
beak_length = 18;
leg_length = 25;
foot_span = 20;

// Wall thickness for 3D printing
wall = 1.5;

module body() {
    // Main body with slight upward curve
    difference() {
        hull() {
            translate([0, 0, body_height/2]) 
                scale([1.2, 0.8, 1]) sphere(d=body_height);
            translate([body_length/3, 0, body_height/2+5]) 
                scale([0.9, 0.7, 0.9]) sphere(d=body_height*0.9);
        }
        // Hollow interior
        translate([0, 0, body_height/2]) 
            scale([1.1, 0.7, 0.9]) sphere(d=body_height-wall*2);
    }
}

module head() {
    // Head with crown
    union() {
        translate([body_length/2+10, 0, body_height+5])
            sphere(d=head_size);

        // Red frontal shield
        translate([body_length/2+14, 0, body_height+7])
            scale([0.3, 0.8, 0.6]) sphere(d=head_size*0.8);

        // Eye
        translate([body_length/2+13, -3, body_height+7])
            sphere(d=2);
    }
}

module beak() {
    // Red beak with slight curve
    translate([body_length/2+beak_length/2+12, 0, body_height+3])
        rotate([0, -15, 0])
            scale([beak_length, head_size/3, head_size/4])
                cylinder(h=1, $fn=8);
}

module wing() {
    // Folded wing
    translate([-body_length/4, 0, body_height/2+8])
        rotate([0, 30, 0])
            scale([body_length/2, body_height/2, wall*2])
                cube([1, 1, 1], center=true);
}

module tail() {
    // Tail feathers
    for(i=[-2:2]) {
        translate([-body_length/2-5, i*3, body_height/2-2])
            rotate([0, 20, i*5])
                scale([1, 0.3, 0.1])
                    cube([10, 8, 2]);
    }
}

module leg(side) {
    // Upper leg
    translate([body_length/6, side*5, body_height/2-10])
        rotate([0, side*10, 0])
            cylinder(h=leg_length/2, d=3, $fn=8);

    // Lower leg
    translate([body_length/6+5, side*7, body_height/2-10-leg_length/2])
        rotate([0, side*15, 0])
            cylinder(h=leg_length/2, d=2.5, $fn=8);
}

module foot(side) {
    // Long toes
    for(i=[-1.5, 0, 1.5]) {
        translate([body_length/6+8, side*7+i*3, body_height/2-10-leg_length])
            rotate([90, 0, 0])
                cylinder(h=foot_span/3, d=2, $fn=8);
    }
}

// Assembly
union() {
    body();
    head();
    beak();
    wing();
    mirror([0, 1, 0]) wing();
    tail();

    // Legs and feet
    leg(1);
    leg(-1);
    foot(1);
    foot(-1);
}

