// Sunfish (Mola mola) – medium-complexity, realistic style
// Designed for FDM printing, 0.4 mm nozzle, 0.2 mm layers
// Overall length ≈ 80 mm, thickness ≈ 12 mm, no steep overhangs

$fa = 1;
$fs = 0.5;

module sunfish() {
    // ---------- Main body ----------
    // Tall oval body, slightly flattened along vertical axis
    scale([1, 0.7, 0.35])
        sphere(d = 60, $fn = 60);

    // Dorsal fin – vertical thin plate
    translate([0, 0, 10.5])
        scale([1, 0.05, 1])
            cylinder(h = 18, d = 28, $fn = 40);

    // Anal fin – mirror of dorsal
    translate([0, 0, -10.5])
        scale([1, 0.05, 1])
            cylinder(h = 18, d = 28, $fn = 40);

    // ---------- Caudal fin (rudder-like) ----------
    translate([-30, 0, 0])
        rotate([0, 90, 0])
            difference() {
                scale([1, 0.8, 1])
                    cylinder(h = 20, d = 20, $fn = 4);
                translate([0, 0, -1])
                    cube([40, 20, 22], center = true);
            }

    // ---------- Eyes ----------
    for (eye = [[11, 21, 4], [11, -21, 4]]) {
        translate(eye)
            sphere(d = 6);
        translate(eye + [1, 0, 2])
            sphere(d = 3);  // pupil
    }

    // ---------- Mouth ----------
    translate([27, 0, -3])
        rotate([0, 90, 0])
            difference() {
                cylinder(h = 22, d = 8, $fn = 30);
                translate([0, 0, -1])
                    cube([30, 10, 10], center = true);
            }

    // ---------- Skin texture – small bumps ----------
    for (i = [0 : 5 : 55]) {
        for (j = [0 : 5 : 359]) {
            theta = j;
            phi = i;
            x = 28 * sin(phi) * cos(theta);
            y = 20 * sin(phi) * sin(theta);
            z = 10 * cos(phi);
            if (abs(y) > 5)  // skip centerline for cleaner print
                translate([x, y, z])
                    sphere(d = 1.5, $fn = 8);
        }
    }
}

sunfish();

