// Takin 3D Model - Realistic Style, Medium Complexity
// Approximate dimensions: 80mm x 40mm x 50mm

// Global parameters
body_length = 60;
body_height = 35;
body_width = 30;

// Main body
module takin_body() {
    // Torso - main bulk
    translate([0, 0, 15])
        scale([1.2, 0.8, 1])
        sphere(d=body_height);

    // Abdomen - slightly smaller
    translate([-10, 0, 10])
        scale([0.9, 0.7, 0.8])
        sphere(d=body_height * 0.8);

    // Chest - more pronounced
    translate([10, 0, 15])
        scale([1, 0.9, 1.1])
        sphere(d=body_height * 0.9);
}

// Head with horns
module takin_head() {
    // Skull base
    translate([body_length/2 + 5, 0, body_height + 5])
        rotate([0, -10, 0])
        scale([1.3, 1, 1])
        sphere(d=20);

    // Snout
    translate([body_length/2 + 15, 0, body_height + 2])
        rotate([0, -10, 0])
        scale([1.5, 0.7, 0.6])
        cylinder(h=15, d1=12, d2=8);

    // Nostrils
    for(side = [-1, 1]) {
        translate([body_length/2 + 20, side * 3, body_height + 1])
            rotate([0, -10, 0])
            sphere(d=3);
    }

    // Horns - curved forward
    for(side = [-1, 1]) {
        translate([body_length/2 + 8, side * 8, body_height + 12])
            rotate([0, 0, side * -20])
            rotate([0, -30, 0])
            cylinder(h=15, d1=5, d2=2);

        // Horn tips
        translate([body_length/2 + 18, side * 10, body_height + 18])
            rotate([0, 0, side * -30])
            sphere(d=3);
    }

    // Ears
    for(side = [-1, 1]) {
        translate([body_length/2 + 5, side * 9, body_height + 15])
            rotate([0, 0, side * 20])
            scale([0.3, 1, 1.5])
            cylinder(h=8, d=6);
    }
}

// Leg module for all four legs
module takin_leg(x_pos, y_pos, angle) {
    translate([x_pos, y_pos, 0])
        rotate([0, 0, angle]) {
            // Upper leg
            translate([0, 0, 8])
                rotate([0, 5, 0])
                scale([0.8, 0.8, 1])
                cylinder(h=15, d=8);

            // Lower leg
            translate([0, 0, 0])
                rotate([0, 10, 0])
                scale([0.7, 0.7, 1])
                cylinder(h=12, d=6);

            // Hoof
            translate([0, 0, -2])
                scale([1.2, 1, 0.5])
                cylinder(h=4, d=8);
        }
}

// Tail
module takin_tail() {
    translate([-body_length/2 - 5, 0, 18])
        rotate([0, 0, 45])
        cylinder(h=12, d=5);

    // Tail tuft
    translate([-body_length/2 - 15, 0, 22])
        sphere(d=6);
}

// Mane ridge along back
module takin_mane() {
    for(i = [-20:5:20]) {
        translate([i, 0, body_height + 15 + abs(i)/5])
            scale([0.5, 0.3, 1])
            sphere(d=6);
    }
}

// Assembly
union() {
    takin_body();
    takin_head();
    takin_mane();

    // Front legs
    takin_leg(body_length/3, body_width/2 - 5, 0);
    takin_leg(body_length/3, -body_width/2 + 5, 0);

    // Rear legs
    takin_leg(-body_length/3, body_width/2 - 5, 0);
    takin_leg(-body_length/3, -body_width/2 + 5, 0);

    takin_tail();
}

