// Tayra - Mustelid mammal (Eira barbara) - Medium complexity realistic style
// Approximate size: 80mm body length

// Body dimensions
body_len = 50;
body_h = 25;
body_w = 20;
head_len = 20;
head_h = 18;
head_w = 16;

// Leg dimensions
leg_len = 15;
leg_w = 6;
leg_h = 6;

// Tail dimensions
tail_len = 30;
tail_w = 8;
tail_h = 8;

// Ear dimensions
ear_r = 4;
ear_h = 6;

// Snout dimensions
snout_len = 8;
snout_w = 10;
snout_h = 7;

// Overall scale for printing
scale_factor = 0.8;

module tayra_body() {
    // Main body - elongated oval shape using hull of spheres
    hull() {
        translate([-body_len/2, 0, 0]) 
            scale([1.2, 1, 1]) sphere(d=body_w);
        translate([body_len/2, 0, 0]) 
            scale([1.2, 1, 1]) sphere(d=body_w);
    }

    // Belly bulge
    translate([0, 0, -body_h/2]) 
        scale([1, 1, 0.8]) 
        sphere(d=body_w*1.2);
}

module tayra_head() {
    // Main head shape
    hull() {
        translate([body_len/2, 0, body_h/3]) 
            sphere(d=head_w);
        translate([body_len/2 + head_len, 0, body_h/3]) 
            sphere(d=head_w*0.8);
    }

    // Snout
    hull() {
        translate([body_len/2 + head_len, 0, body_h/3]) 
            sphere(d=snout_w);
        translate([body_len/2 + head_len + snout_len, 0, body_h/3]) 
            sphere(d=snout_w*0.7);
    }
}

module tayra_ears() {
    // Left ear
    translate([body_len/2 + head_len*0.3, head_w/2, body_h/3 + head_h/2]) 
        rotate([0, 30, 0]) 
        cylinder(h=ear_h, r1=ear_r, r2=ear_r*0.5, $fn=8);

    // Right ear
    translate([body_len/2 + head_len*0.3, -head_w/2, body_h/3 + head_h/2]) 
        rotate([0, -30, 0]) 
        cylinder(h=ear_h, r1=ear_r, r2=ear_r*0.5, $fn=8);
}

module tayra_legs() {
    // Front left leg
    translate([body_len/3, body_w/2 + leg_w/2, -body_h/2]) 
        rotate([15, 0, 0]) 
        cylinder(h=leg_len, r=leg_w/2, $fn=8);

    // Front right leg
    translate([body_len/3, -body_w/2 - leg_w/2, -body_h/2]) 
        rotate([15, 0, 0]) 
        cylinder(h=leg_len, r=leg_w/2, $fn=8);

    // Rear left leg
    translate([-body_len/3, body_w/2 + leg_w/2, -body_h/2]) 
        rotate([15, 0, 0]) 
        cylinder(h=leg_len, r=leg_w/2, $fn=8);

    // Rear right leg
    translate([-body_len/3, -body_w/2 - leg_w/2, -body_h/2]) 
        rotate([15, 0, 0]) 
        cylinder(h=leg_len, r=leg_w/2, $fn=8);
}

module tayra_tail() {
    // Tail with slight upward curve
    for(i=[0:5]) {
        rotate([0, 0, 5*i]) 
        translate([-body_len/2 - tail_len/2 + i*tail_len/6, 0, i*2]) 
            scale([1 - i*0.1, 1 - i*0.1, 1 - i*0.1]) 
            sphere(d=tail_w);
    }
}

module tayra_eyes() {
    // Left eye
    translate([body_len/2 + head_len + snout_len/2, snout_w/3, body_h/3 + head_h/4]) 
        sphere(d=2);

    // Right eye
    translate([body_len/2 + head_len + snout_len/2, -snout_w/3, body_h/3 + head_h/4]) 
        sphere(d=2);
}

module tayra_nose() {
    // Nose at tip of snout
    translate([body_len/2 + head_len + snout_len, 0, body_h/3]) 
        sphere(d=3);
}

module tayra_whiskers() {
    // Whiskers on left side
    for(i=[1:3]) {
        translate([body_len/2 + head_len + snout_len, snout_w/2, body_h/3]) 
            rotate([0, 0, 30*i]) 
            cylinder(h=snout_len/2, r=0.5, $fn=4);
    }

    // Whiskers on right side
    for(i=[1:3]) {
        translate([body_len/2 + head_len + snout_len, -snout_w/2, body_h/3]) 
            rotate([0, 0, -30*i]) 
            cylinder(h=snout_len/2, r=0.5, $fn=4);
    }
}

// Assemble the tayra
scale([scale_factor, scale_factor, scale_factor]) {
    union() {
        tayra_body();
        tayra_head();
        tayra_ears();
        tayra_legs();
        tayra_tail();
        tayra_eyes();
        tayra_nose();
        tayra_whiskers();
    }
}

