// Tern (medium complexity, realistic style)
// A medium-sized seabird with distinctive forked tail and sharp beak

// Overall scale factor for 3D printing
scale_factor = 0.4;

// Main module to build the entire tern
module tern() {
    scale([scale_factor, scale_factor, scale_factor]) {
        union() {
            // Body
            body();

            // Head
            translate([0, 18, 8]) head();

            // Wings
            wings();

            // Tail
            tail();

            // Legs
            translate([-3, -22, 0]) leg(true);
            translate([3, -22, 0]) leg(false);
        }
    }
}

// Body of the tern
module body() {
    // Main oval body
    translate([0, 0, 6])
        scale([1, 1.8, 1])
        sphere(r = 10, $fn = 32);

    // Chest protrusion
    translate([0, 8, 5])
        rotate([20, 0, 0])
        scale([0.7, 1, 0.8])
        cylinder(h = 8, r1 = 8, r2 = 5, $fn = 24);

    // Back hump
    translate([0, -5, 9])
        scale([1.1, 0.8, 0.6])
        sphere(r = 8, $fn = 24);
}

// Head with beak and eyes
module head() {
    // Main head sphere
    sphere(r = 7, $fn = 24);

    // Beak
    translate([0, 8, -2])
        rotate([20, 0, 0])
        scale([0.3, 1.8, 0.3])
        cylinder(h = 12, r1 = 3, r2 = 0.5, $fn = 8);

    // Beak tip color
    translate([0, 15, -1.5])
        scale([0.3, 0.5, 0.3])
        cylinder(h = 2, r = 0.8, $fn = 8);

    // Eyes
    translate([-3, 4, 2])
        sphere(r = 1.2, $fn = 12);
    translate([3, 4, 2])
        sphere(r = 1.2, $fn = 12);

    // Eye pupils
    translate([-3.5, 4.5, 2.5])
        sphere(r = 0.5, $fn = 8);
    translate([3.5, 4.5, 2.5])
        sphere(r = 0.5, $fn = 8);

    // Black cap
    difference() {
        translate([0, 2, 3])
            scale([1.1, 1.2, 0.8])
            sphere(r = 7.5, $fn = 24);

        // Cut to shape
        translate([0, -2, 2])
            cube([20, 10, 10], center = true);
    }
}

// Wings module
module wings() {
    // Left wing
    translate([-12, -2, 8])
        rotate([0, 0, 30])
        wing(true);

    // Right wing
    translate([12, -2, 8])
        rotate([0, 0, -30])
        wing(false);
}

// Single wing
module wing(left = true) {
    mirror = left ? 1 : -1;

    // Main wing
    translate([mirror * 5, 0, 0])
        rotate([0, 0, mirror * -10])
        scale([mirror * 1, 1, 0.3])
        cylinder(h = 22, r1 = 8, r2 = 2, $fn = 3);

    // Wing tip
    translate([mirror * 18, -5, 0])
        rotate([0, 0, mirror * 20])
        scale([mirror * 0.8, 1, 0.3])
        cylinder(h = 8, r1 = 3, r2 = 1, $fn = 3);

    // Wing feathers (simplified)
    for (i = [0:2]) {
        translate([mirror * (10 + i * 4), -2 - i * 1.5, -1])
            rotate([0, 0, mirror * (15 + i * 5)])
            scale([mirror * 0.3, 1, 0.1])
            cylinder(h = 6, r1 = 2, r2 = 0.5, $fn = 3);
    }
}

// Forked tail
module tail() {
    // Central tail base
    translate([0, -18, 6])
        rotate([30, 0, 0])
        scale([0.5, 1, 0.3])
        cylinder(h = 12, r1 = 4, r2 = 1, $fn = 8);

    // Left tail feather
    translate([-3, -25, 3])
        rotate([20, 0, -15])
        scale([0.3, 1.2, 0.1])
        cylinder(h = 15, r1 = 2, r2 = 0.5, $fn = 3);

    // Right tail feather
    translate([3, -25, 3])
        rotate([20, 0, 15])
        scale([0.3, 1.2, 0.1])
        cylinder(h = 15, r1 = 2, r2 = 0.5, $fn = 3);
}

// Leg and foot
module leg(left = true) {
    mirror = left ? 1 : -1;

    // Upper leg
    translate([0, 0, 5])
        rotate([20, 0, mirror * 10])
        cylinder(h = 8, r = 1.5, $fn = 8);

    // Lower leg
    translate([mirror * 2, -3, 0])
        rotate([60, 0, mirror * 15])
        cylinder(h = 6, r = 1.2, $fn = 8);

    // Foot
    translate([mirror * 3, -8, -1])
        rotate([90, 0, 0])
        scale([1.5, 0.3, 1])
        cylinder(h = 4, r = 1, $fn = 8);

    // Toes
    for (i = [-1:1]) {
        translate([mirror * (3 + i * 1.5), -8, -1])
            rotate([90, 0, i * 30])
            scale([1, 0.2, 0.5])
            cylinder(h = 3, r = 0.5, $fn = 6);
    }
}

// Generate the tern
tern();

