// Realistic Thrasher (medium complexity)
// Approx 80 mm long, 40 mm wide, 35 mm high

// Body parameters
body_len = 60;
body_wid = 30;
body_hgt = 20;
body_rad = 12;

// Head parameters
head_len = 22;
head_wid = 18;
head_hgt = 17;

// Bill parameters
bill_len = 28;
bill_wid = 7;
bill_hgt = 7;

// Tail parameters
tail_len = 18;
tail_wid = 35;
tail_hgt = 4;

// Leg parameters
leg_len = 12;
leg_wid = 3;
leg_hgt = 5;

// Eye parameters
eye_rad = 3.5;

// Wing parameters
wing_len = 25;
wing_wid = 15;

$fn = 24;

module thrasher_body() {
    difference() {
        // Main body ellipse
        scale([body_len/2, body_wid/2, body_hgt/2])
            sphere(r = body_rad);

        // Flatten underside
        translate([0, 0, -body_hgt/2 - 1])
            cube([body_len + 2, body_wid + 2, body_hgt], center = true);
    }
}

module head() {
    difference() {
        union() {
            // Main head
            translate([body_len/2 - 5, 0, body_hgt/2 - 2])
                scale([head_len/2, head_wid/2, head_hgt/2])
                    sphere(r = head_rad = 10);

            // Crest
            translate([body_len/2 + head_len/2 - 8, 0, body_hgt/2 + 8])
                rotate([0, 20, 0])
                    scale([1.2, 1, 1])
                        cylinder(h = 12, r1 = 4, r2 = 2);
        }

        // Eye sockets
        translate([body_len/2 + head_len/2 - 12, head_wid/2 - 3, body_hgt/2 + 2])
            sphere(r = eye_rad + 0.5);
        translate([body_len/2 + head_len/2 - 12, -head_wid/2 + 3, body_hgt/2 + 2])
            sphere(r = eye_rad + 0.5);
    }
}

module bill() {
    hull() {
        // Base
        translate([body_len/2 + head_len - 3, 0, body_hgt/2 - 1])
            sphere(r = bill_wid/2);

        // Tip
        translate([body_len/2 + head_len + bill_len - 8, 0, body_hgt/2 - 1])
            sphere(r = 1.5);
    }
}

module tail() {
    difference() {
        // Main tail
        translate([-body_len/2 - tail_len/2 + 2, 0, body_hgt/2 - 2])
            scale([tail_len, tail_wid, tail_hgt])
                sphere(r = 6);

        // Feathers cut
        for (i = [-2:2])
            translate([-body_len/2 - tail_len/2 + 2, i * 5, body_hgt/2 - 2])
                rotate([0, 0, i * 5])
                    cube([tail_len + 2, 2, tail_hgt + 2], center = true);
    }
}

module wing() {
    difference() {
        // Upper wing
        translate([0, body_wid/2 + wing_wid/4 - 2, body_hgt/2 + 2])
            rotate([0, 0, 20])
                scale([wing_len, wing_wid, 3])
                    sphere(r = 5);

        // Wing cut
        translate([0, body_wid/2 + wing_wid/2, body_hgt/2 + 2])
            cube([wing_len + 5, wing_wid, 5], center = true);
    }
}

module eye() {
    color("black")
    translate([body_len/2 + head_len/2 - 12, head_wid/2 - 3, body_hgt/2 + 2])
        sphere(r = eye_rad);
    translate([body_len/2 + head_len/2 - 12, -head_wid/2 + 3, body_hgt/2 + 2])
        sphere(r = eye_rad);
}

module legs() {
    // Left leg
    translate([body_len/4 - 5, body_wid/4 - 2, -body_hgt/2 - leg_len/2])
        cube([leg_wid, leg_wid, leg_len], center = true);
    translate([body_len/4 - 5 - leg_wid/2, body_wid/4 - 2 - leg_wid/2, -body_hgt/2 - leg_len])
        cube([leg_wid * 2, leg_wid * 2, leg_hgt], center = true);

    // Right leg
    translate([body_len/4 - 5, -body_wid/4 + 2, -body_hgt/2 - leg_len/2])
        cube([leg_wid, leg_wid, leg_len], center = true);
    translate([body_len/4 - 5 - leg_wid/2, -body_wid/4 + 2 + leg_wid/2, -body_hgt/2 - leg_len])
        cube([leg_wid * 2, leg_wid * 2, leg_hgt], center = true);
}

// Assembly
union() {
    thrasher_body();
    head();
    bill();
    tail();

    // Left wing
    wing();

    // Right wing (mirrored)
    mirror([0, 1, 0])
        wing();

    legs();
}

// Eyes added after main union
eye();

