// Terrier in medium-complexity realistic style
// Overall size approximately 70 mm from nose to tail

$fn = 32;
body_len = 45;
body_w   = 22;
body_h   = 20;

head_len = 15;
head_w   = 14;
head_h   = 12;

leg_h    = 16;
leg_w    = 5;

tail_len = 20;
tail_r   = 3;

ear_len  = 8;
ear_w    = 4;

muzzle_len = 7;
muzzle_r   = 4;

// Body
module body() {
    translate([0, 0, leg_h]) {
        difference() {
            hull() {
                // Main torso
                translate([0, 0, body_h/2])
                    scale([1.0, 0.7, 0.6])
                    sphere(d = body_len);

                // Rounded rear
                translate([-body_len/2 + 5, 0, 0])
                    scale([0.8, 0.9, 0.7])
                    sphere(d = body_w);

                // Chest
                translate([body_len/2 - 4, 0, -body_h/4])
                    scale([0.9, 0.8, 0.7])
                    sphere(d = body_w);
            }

            // Belly cutout for printability
            translate([0, 0, -2])
                cube([body_len + 2, body_w + 2, 4], center = true);
        }
    }
}

// Head
module head() {
    translate([body_len/2 + head_len/2, 0, body_h + leg_h - 2]) {
        rotate([0, 0, 10]) {
            // Skull
            difference() {
                hull() {
                    sphere(d = head_w);
                    translate([head_len/2 - 2, 0, 0])
                        sphere(d = head_w * 0.9);
                }
                // Eye sockets
                for (i = [-1:1:1]) {
                    translate([head_len/2 * 0.4, i * head_w/4, head_h/4])
                        sphere(d = 3, $fn = 12);
                }
            }

            // Muzzle
            translate([head_len/2 + muzzle_len/2, 0, -head_h/3])
                rotate([0, 10, 0])
                scale([1.3, 1, 0.9])
                sphere(d = muzzle_r * 2);

            // Nose
            translate([head_len/2 + muzzle_len + 1, 0, -head_h/3])
                sphere(d = 3);
        }
    }
}

// Ears
module ears() {
    for (i = [-1:1:1]) {
        translate([body_len/2 + head_len/2 + 3, i * head_w/2, body_h + leg_h + 2])
            rotate([0, i * 20, 90 + i * 15])
            linear_extrude(height = ear_len, scale = 0.2)
            offset(r = 1)
            square([ear_w, ear_len], center = true);
    }
}

// Legs
module legs() {
    for (x = [-body_len/2 + 6, body_len/2 - 4])
        for (y = [-body_w/2 + 3, body_w/2 - 3]) {
            translate([x, y, leg_h/2])
                cylinder(d1 = leg_w + 1, d2 = leg_w - 1, h = leg_h);
            // Paws
            translate([x, y, 1])
                sphere(d = leg_w + 1);
        }
}

// Tail
module tail() {
    translate([-body_len/2, 0, leg_h + body_h/2])
        rotate([0, 0, -20])
        rotate([80, 0, 0])
        cylinder(d1 = tail_r * 2, d2 = tail_r, h = tail_len);
}

// Eyes
module eyes() {
    for (i = [-1:1:1]) {
        translate([body_len/2 + head_len/2 + 2, i * head_w/4, body_h + leg_h])
            sphere(d = 2);
    }
}

// Assembly
union() {
    body();
    head();
    ears();
    legs();
    tail();
    eyes();
}

