// Dimensions: ~80mm long, ~60mm wide, ~30mm high

$fn = 32;  // Smooth curves for realistic appearance

module terrapin() {
    // Main shell (carapace)
    union() {
        // Upper shell dome
        translate([0, 0, 12]) {
            scale([1.2, 1, 0.7]) {
                sphere(d=50);
            }
        }

        // Shell rim
        difference() {
            cylinder(h=8, d1=55, d2=50);
            translate([0, 0, -1]) {
                cylinder(h=10, d1=45, d2=40);
            }
        }

        // Shell segments (scutes)
        for(i = [-2:2]) {
            for(j = [-1:1]) {
                if(!(abs(i) == 2 && abs(j) == 1)) {
                    translate([i*8, j*10, 12]) {
                        scale([0.8, 0.6, 0.1]) {
                            sphere(d=15);
                        }
                    }
                }
            }
        }
    }

    // Head
    translate([25, 0, 8]) {
        rotate([0, -10, 0]) {
            scale([1.2, 0.8, 0.8]) {
                sphere(d=15);
            }
        }

        // Snout
        translate([7, 0, 0]) {
            scale([1, 0.7, 0.6]) {
                sphere(d=8);
            }
        }

        // Eyes
        for(side = [-1, 1]) {
            translate([5, side*4, 3]) {
                sphere(d=3);
            }
        }
    }

    // Neck
    translate([15, 0, 5]) {
        rotate([0, -20, 0]) {
            cylinder(h=12, d1=8, d2=12);
        }
    }

    // Front flippers
    for(side = [-1, 1]) {
        translate([8, side*20, 2]) {
            rotate([0, 0, side*30]) {
                scale([1.2, 0.3, 0.2]) {
                    difference() {
                        sphere(d=25);
                        translate([0, 0, -15]) cube([30, 30, 30], center=true);
                    }
                }
            }

            // Flipper claws
            for(i = [0:2]) {
                translate([-8 + i*4, side*12, 0]) {
                    rotate([0, 90, 0]) {
                        cylinder(h=3, d1=2, d2=1);
                    }
                }
            }
        }
    }

    // Rear flippers
    for(side = [-1, 1]) {
        translate([-18, side*18, 2]) {
            rotate([0, 0, side*45]) {
                scale([1, 0.3, 0.2]) {
                    difference() {
                        sphere(d=20);
                        translate([0, 0, -15]) cube([25, 25, 25], center=true);
                    }
                }
            }

            // Rear claws
            for(i = [0:1]) {
                translate([-5 + i*4, side*10, 0]) {
                    rotate([0, 90, 0]) {
                        cylinder(h=2.5, d1=1.5, d2=1);
                    }
                }
            }
        }
    }

    // Tail
    translate([-25, 0, 2]) {
        rotate([0, 0, -10]) {
            scale([0.5, 0.5, 1]) {
                difference() {
                    sphere(d=15);
                    translate([0, 0, -10]) cube([20, 20, 20], center=true);
                }
            }
        }
    }

    // Lower shell (plastron)
    difference() {
        translate([0, 0, 2]) {
            scale([1, 0.9, 0.3]) {
                sphere(d=45);
            }
        }
        translate([0, 0, -1]) {
            cylinder(h=10, d=35);
        }
    }

    // Leg joints
    for(pos = [[12, 18], [12, -18], [-12, 16], [-12, -16]]) {
        translate([pos[0], pos[1], 4]) {
            sphere(d=6);
        }
    }
}

