                linear_extrude(height=2)
                    polygon(points=[[0,0], [-15,-12], [-15,12]]);
    }
}

module dorsal_fin_spines() {
    // Dorsal fin with spines
    for(i = [-20:5:15]) {
        translate([i, 0, 8])
            rotate([0, 90, 0])
                cylinder(h=10, r1=1, r2=0.5);
    }
}

module dorsal_fin_web() {
    // Web between dorsal spines
    hull() {
        translate([-20, 0, 8])
            sphere(r=1);
        translate([15, 0, 8])
            sphere(r=1);
    }
}

module pectoral_fin() {
