// Tomtit - realistic style, medium complexity
// Designed for 3D printing - no overhangs, 0.8mm walls

$fn = 32;
body_length = 45;
body_height = 22;
body_width = 18;

module body() {
    difference() {
        // Main body - ellipsoid shape
        scale([1, 0.8, 0.6])
            sphere(r=body_length/2);

        // Flatten bottom for printing
        translate([0, 0, -body_height/2])
            cube([body_length*2, body_width*2, body_height], center=true);
    }
}

module head() {
    translate([body_length/2 - 5, 0, body_height/2 - 3]) {
        // Head sphere
        sphere(r=8);

        // Beak - cone shape
        translate([8, 0, -2]) {
            rotate([0, -20, 0])
                cylinder(h=12, r1=2, r2=0);
        }

        // Eye
        translate([5, -4, 2]) {
            color("black")
                sphere(r=1.5);
        }
        translate([5, 4, 2]) {
            color("black")
                sphere(r=1.5);
        }
    }
}

module wing() {
    // Curved wing shape
    translate([0, 0, body_height/2 - 2]) {
        rotate([0, 0, -10])
            scale([1.2, 0.2, 0.8])
                sphere(r=body_length/3);
    }
}

module tail() {
    // Fan tail - multiple feathers
    translate([-body_length/2 - 2, 0, body_height/2 - 5]) {
        for(i = [-1:0.5:1]) {
            rotate([0, 0, i * 25])
                translate([i * 2, 0, 0])
                    scale([1.5, 0.3, 0.8])
                        cylinder(h=12, r1=5, r2=0);
        }
    }
}

module legs() {
    // Left leg
    translate([body_length/2 - 10, -5, -body_height/2 + 2]) {
        // Upper leg
        cylinder(h=6, r=1.5);

        // Lower leg
        translate([0, 0, -6])
            rotate([10, 0, 0])
                cylinder(h=8, r=1.2);

        // Foot - 3 toes
        translate([0, -2, -13])
            for(j = [-1, 0, 1]) {
                translate([j * 2, 0, 0])
                    rotate([0, 90, 0])
                        cylinder(h=4, r=0.8);
            }
    }

    // Right leg
    translate([body_length/2 - 10, 5, -body_height/2 + 2]) {
        cylinder(h=6, r=1.5);
        translate([0, 0, -6])
            rotate([10, 0, 0])
                cylinder(h=8, r=1.2);
        translate([0, 2, -13])
            for(j = [-1, 0, 1]) {
                translate([j * 2, 0, 0])
                    rotate([0, 90, 0])
                        cylinder(h=4, r=0.8);
            }
    }
}

module chest() {
    // Round chest area
    translate([body_length/2 - 8, 0, -body_height/4]) {
        scale([0.8, 1, 1.2])
            sphere(r=body_width/2);
    }
}

// Assemble the tomtit
union() {
