    color("black") translate([0, -body_width/2 + 2, 0]) wing();
    color("black") mirror([0, 1, 0]) translate([0, -body_width/2 + 2, 0]) wing();
    color("black") tail();
    color("orange") legs();
    color("whitesmoke") chest();
}

