// Realistic Tit Bird - Medium Complexity
// Dimensions: ~80mm wingspan, ~60mm length, ~45mm height

// Main parameters
body_length = 35;
body_height = 25;
body_width = 22;
head_radius = 12;
beak_length = 8;
beak_width = 3;
beak_height = 3;
tail_length = 18;
tail_height = 15;
tail_width = 12;
wing_length = 25;
wing_width = 12;
wing_thickness = 2;
leg_length = 12;
leg_thickness = 3;
foot_length = 8;
foot_width = 4;

// Body module - teardrop shape
module bird_body() {
    union() {
        // Main body - ellipsoid
        scale([1, 0.8, 0.7])
        sphere(r=body_height/2);

        // Tail connection
        translate([body_length/2 - 5, 0, -body_height/4])
        scale([1.5, 1, 0.6])
        sphere(r=body_height/3);
    }
}

// Head module with realistic features
module bird_head() {
    union() {
        // Main head shape
        translate([-body_length/2 - head_radius + 5, 0, body_height/2])
        sphere(r=head_radius);

        // Beak
        translate([-body_length/2 - head_radius - beak_length/2, 0, body_height/2])
        rotate([0, -15, 0])
        scale([beak_length, beak_width, beak_height])
        cube([1, 1, 1], center=true);

        // Eye sockets
        translate([-body_length/2 - head_radius + 3, head_radius/2, body_height/2 + 2])
        sphere(r=2);
        translate([-body_length/2 - head_radius + 3, -head_radius/2, body_height/2 + 2])
        sphere(r=2);
    }
}

// Wing module with feather details
module bird_wing(side) {
    translate([0, side * body_width/3, body_height/3])
    rotate([0, -20 * side, 0])
    union() {
        // Main wing
        translate([body_length/4, 0, 0])
        scale([wing_length, wing_width, wing_thickness])
        cube([1, 1, 1], center=true);

        // Wing tip
        translate([body_length/4 + wing_length/2, 0, 0])
        scale([wing_length/3, wing_width/2, wing_thickness])
        sphere(r=1);

        // Feather details
        for(i = [0:2]) {
            translate([body_length/4 + i * wing_length/3, side * (wing_width/2 + 1), 0])
            scale([wing_length/5, 1, wing_thickness/2])
            cube([1, 1, 1], center=true);
        }
    }
}

// Tail module
module bird_tail() {
    translate([body_length/2, 0, -body_height/4])
    rotate([30, 0, 0])
    union() {
        // Main tail
        scale([tail_length, tail_width, tail_height])
        cube([1, 1, 1], center=true);

        // Tail feathers
        for(i = [-1:1]) {
            translate([tail_length/2, i * tail_width/3, 0])
            scale([tail_length/3, 1, 2])
            cube([1, 1, 1], center=true);
        }
    }
}

// Leg and foot module
module bird_leg(side) {
    translate([-body_length/4, side * body_width/4, -body_height/2 - leg_length/2])
    union() {
        // Leg
        cylinder(h=leg_length, r=leg_thickness/2, $fn=8);

        // Foot
        translate([0, 0, -leg_length/2 - foot_width/2])
        rotate([90, 0, 0])
        union() {
            // Main foot
            scale([foot_length, foot_width, leg_thickness/2])
            cube([1, 1, 1], center=true);

            // Toes
            for(i = [-1, 0, 1]) {
                translate([foot_length/2 - 2, i * foot_width/3, 0])
                scale([foot_length/3, foot_width/4, leg_thickness/2])
                cube([1, 1, 1], center=true);
            }
        }
    }
}

// Chest feather detail
module chest_feathers() {
    translate([-body_length/4, 0, -body_height/3])
    for(i = [0:3]) {
        for(j = [-1:1]) {
            translate([i * body_length/8, j * body_width/6, -i * 2])
            scale([2, 2, 1])
            sphere(r=1.5);
        }
    }
}

// Main assembly
union() {
    // Body
    bird_body();

    // Head
    bird_head();

    // Wings
    bird_wing(1);  // Right wing
    bird_wing(-1); // Left wing

    // Tail
    bird_tail();

    // Legs and feet
    bird_leg(1);   // Right leg
    bird_leg(-1);  // Left leg

    // Chest detail
    chest_feathers();
}

