// Tsetse Fly - Realistic Style, Medium Complexity
// Approx 80mm wingspan, 3D printable

$fn = 24;

// Main parameters
body_length = 30;
body_diameter = 8;
head_length = 12;
head_diameter = 10;
wing_span = 80;
wing_thickness = 0.8;
leg_length = 15;
leg_diameter = 1.5;

// Colors (for preview)
color_body = "darkbrown";
color_wings = "lightgray";
color_legs = "black";

// Main module
module tsetse_fly() {
    union() {
        // Body segments
        thorax();
        abdomen();
        head();

        // Wings
        left_wing();
        right_wing();

        // Legs
        legs();

        // Proboscis
        proboscis();
    }
}

// Thorax segment (main body part)
module thorax() {
    translate([0, 0, 0])
    color(color_body)
    union() {
        // Main thorax
        scale([1.2, 1, 1])
        sphere(d = body_diameter);

        // Segmentation rings
        for(i = [1:3]) {
            translate([0, 0, -i*2])
            cylinder(h = 0.5, d1 = body_diameter*1.1, d2 = body_diameter*0.9);
        }
    }
}

// Abdomen segment
module abdomen() {
    translate([0, 0, -body_length/2])
    color(color_body)
    hull() {
        sphere(d = body_diameter);
        translate([0, 0, -body_length/2])
        sphere(d = body_diameter*0.7);
    }
}

// Head with compound eyes
module head() {
    translate([0, 0, body_length/2 + 5])
    color(color_body)
    union() {
        // Main head
        sphere(d = head_diameter);

        // Compound eyes
        for(side = [-1, 1]) {
            translate([side*4, head_diameter/2 - 1, 2])
            scale([1.2, 1, 1])
            sphere(d = 4);
        }

        // Antennae
        for(side = [-1, 1]) {
            translate([side*3, 0, head_diameter/2])
            rotate([0, 90, side*10])
            cylinder(h = 8, d1 = 0.5, d2 = 0.2);
        }
    }
}

// Wing generation module
module wing() {
    difference() {
        // Main wing shape
        hull() {
            translate([0, 0, 0])
            sphere(d = 3);
            translate([0, wing_span/2 - 10, -5])
            sphere(d = 2);
            translate([0, wing_span/2, 0])
            sphere(d = 1);
            translate([0, wing_span/2 - 5, 5])
            sphere(d = 2);
        }

        // Wing cells pattern
        for(i = [1:5]) {
            translate([0, i*8, -3+i])
            rotate([0, 0, 45])
            cube([wing_thickness*2, 6, 0.5], center = true);
        }
    }
}

// Left wing
module left_wing() {
    translate([0, body_diameter/2, body_length/4])
    rotate([0, 0, -30])
    color(color_wings)
    scale([0.8, 1, wing_thickness])
    wing();
}

// Right wing
module right_wing() {
    mirror([0, 1, 0])
    left_wing();
}

// Leg module
module single_leg(angle, offset) {
    translate([0, offset, 0])
    rotate([angle, 0, 0])
    color(color_legs)
    union() {
        cylinder(h = leg_length, d = leg_diameter);
        translate([0, 0, leg_length])
        sphere(d = leg_diameter*1.5);
    }
}

// All legs
module legs() {
    // Front legs
    for(side = [-1, 1]) {
        translate([side*3, 0, body_length/3])
        rotate([0, 0, side*30])
        single_leg(-45, side*2);
    }

    // Middle legs
    for(side = [-1, 1]) {
        translate([side*2.5, 0, 0])
        rotate([0, 0, side*45])
        single_leg(-60, side*3);
    }

    // Rear legs
    for(side = [-1, 1]) {
        translate([side*2, 0, -body_length/3])
        rotate([0, 0, side*60])
        single_leg(-75, side*4);
    }
}

// Proboscis (feeding tube)
module proboscis() {
    translate([0, 0, body_length/2 + head_diameter/2 + 2])
    rotate([90, 0, 0])
    color(color_body)
    union() {
        cylinder(h = 15, d1 = 2, d2 = 0.5);
        translate([0, 0, 15])
        sphere(d = 1);
    }
}

// Generate the model
tsetse_fly();

