// Approx 80mm long, 3D printable

// Overall scale factor
scale_factor = 0.8;

module toad_body() {
    // Main body - ellipsoid shape
    translate([0, 0, 30 * scale_factor])
    scale([1.2, 1, 0.8])
    sphere(25 * scale_factor, $fn=32);

    // Lower belly bulge
    translate([0, 0, 15 * scale_factor])
    scale([1.3, 1.1, 0.4])
    sphere(20 * scale_factor, $fn=32);
}

module toad_head() {
    // Head - wider and flatter
    translate([35 * scale_factor, 0, 35 * scale_factor])
    scale([0.9, 1.2, 0.7])
    sphere(20 * scale_factor, $fn=32);

    // Snout protrusion
    translate([50 * scale_factor, 0, 32 * scale_factor])
    scale([0.5, 0.8, 0.6])
    sphere(12 * scale_factor, $fn=32);
}

module eyes() {
    // Left eye
    translate([38 * scale_factor, -15 * scale_factor, 42 * scale_factor]) {
        // Eye socket
        sphere(8 * scale_factor, $fn=16);

        // Pupil
        translate([4 * scale_factor, 0, 2 * scale_factor])
        scale([0.5, 1, 1])
        sphere(5 * scale_factor, $fn=16);
    }

    // Right eye - mirrored
    translate([38 * scale_factor, 15 * scale_factor, 42 * scale_factor]) {
        sphere(8 * scale_factor, $fn=16);
        translate([4 * scale_factor, 0, 2 * scale_factor])
        scale([0.5, 1, 1])
        sphere(5 * scale_factor, $fn=16);
    }
}

module legs() {
    // Front legs
    for(side = [-1, 1]) {
        // Upper arm
        translate([25 * scale_factor, side * 20 * scale_factor, 25 * scale_factor])
        rotate([0, 45, 0])
        cylinder(h=15 * scale_factor, r=5 * scale_factor, $fn=12);

        // Forearm
        translate([35 * scale_factor, side * 25 * scale_factor, 15 * scale_factor])
        rotate([0, 30, 20 * side])
        cylinder(h=12 * scale_factor, r=4 * scale_factor, $fn=12);

        // Hand
        translate([42 * scale_factor, side * 28 * scale_factor, 10 * scale_factor])
        sphere(6 * scale_factor, $fn=12);
    }

    // Hind legs
    for(side = [-1, 1]) {
        // Thigh
        translate([-25 * scale_factor, side * 15 * scale_factor, 25 * scale_factor])
        rotate([45, 0, -20 * side])
        cylinder(h=20 * scale_factor, r=7 * scale_factor, $fn=12);

        // Lower leg
        translate([-35 * scale_factor, side * 25 * scale_factor, 15 * scale_factor])
        rotate([30, 0, -30 * side])
        cylinder(h=18 * scale_factor, r=6 * scale_factor, $fn=12);

        // Foot
        translate([-45 * scale_factor, side * 30 * scale_factor, 8 * scale_factor])
        scale([1.5, 1, 0.5])
        sphere(8 * scale_factor, $fn=12);
    }
}

module warts() {
    // Random warts on back
    for(i = [0:5:35]) {
        angle = i * 10;
        radius = 15 + sin(i) * 5;

        translate([
            cos(angle) * radius * scale_factor * 0.5,
            sin(angle) * radius * scale_factor * 0.4,
            45 * scale_factor
        ])
        sphere(2 * scale_factor, $fn=8);
    }

    // Additional warts
    for(pos = [[20, 10], [10, -15], [-10, 12], [-20, -8]]) {
        translate([pos[0] * scale_factor, pos[1] * scale_factor, 48 * scale_factor])
        sphere(1.5 * scale_factor, $fn=8);
    }
}

module mouth() {
    // Mouth opening
    difference() {
        union() {
            toad_head();
            toad_body();
        }

        // Mouth cavity
        translate([52 * scale_factor, 0, 28 * scale_factor])
        rotate([0, -20, 0])
        scale([0.8, 1.5, 0.6])
        sphere(15 * scale_factor, $fn=32);
    }
}

// Combine all parts
union() {
    mouth();
    eyes();
    legs();
    warts();
}

// Add flat bottom for stability
translate([0, 0, 5 * scale_factor])
cube([80 * scale_factor, 60 * scale_factor, 10 * scale_factor], center=true);

