$fn = 50;

module body() {
    // Main body using scaled spheres for streamlined shape
    hull() {
        translate([0,0,0]) sphere(r=15);
        translate([-30,0,0]) scale([1.4,1,1]) sphere(r=10);
        translate([40,0,0]) scale([0.9,0.8,0.8]) sphere(r=12);
    }
}

module tail() {
    // Forked tail fin
    rotate([0,-10,0]) 
    hull() {
        translate([55,0,0]) rotate([90,0,0]) cylinder(h=1, r1=12, r2=0);
        translate([55,0,0]) rotate([90,0,30]) cylinder(h=1, r1=12, r2=0);
        translate([55,0,0]) rotate([90,0,-30]) cylinder(h=1, r1=12, r2=0);
    }
}

module dorsal_fin() {
    // Dorsal fin on top
    translate([-15,0,12]) 
    rotate([90,0,0]) 
    linear_extrude(height=1, center=true) 
    polygon(points=[[0,0], [15,15], [30,0]]);
}

