// Medium complexity 3D-printable tuna (realistic style)
// Overall length ~75 mm

$fn = 24;              // smooth curves
body_len = 60;
body_dia = 25;

module tuna_body() {
    // main fusiform body
    hull() {
        // front taper
        translate([body_len/2, 0, 0]) 
            scale([1, 0.7, 0.7]) 
                sphere(d = body_dia);

        // mid section
        translate([0, 0, 0]) 
            rotate([0, 90, 0]) 
                cylinder(h = body_len, d = body_dia);

        // tail taper
        translate([-body_len/2-2, 0, 0]) 
            scale([1, 0.45, 0.45]) 
                sphere(d = body_dia);
    }
}

module dorsal_fin() {
    // first dorsal
    color("gray")
    translate([-body_len*0.15, 0, body_dia/2-0.5]) {
        rotate([90, 0, 0]) {
            linear_extrude(height = 0.8) {
                polygon(points = [
                    [0, 0],
                    [10, 0],
                    [5, 12],
                    [2, 11],
                    [0, 5]
                ]);
            }
        }
    }

    // second dorsal
    translate([-body_len*0.42, 0, body_dia/2-0.5]) {
        rotate([90, 0, 0]) {
            linear_extrude(height = 0.8) {
                polygon(points = [
                    [0, 0],
                    [7, 0],
                    [3, 8],
                    [1, 7],
                    [0, 3]
                ]);
            }
        }
    }
}

module pectoral_fin() {
    // left fin
    translate([body_len*0.2, body_dia/2+0.5, -body_dia/4]) {
        rotate([0, 0, 25])
            scale([1, 0.2, 0.05])
                rotate([0, 90, 0]) {
                    cylinder(h = 12, d = 8);
                    translate([6, 0, 0])
                        sphere(d = 8);
                }
    }
    // right fin (mirror)
    mirror([0, 1, 0])  pectoral_fin();
}

module tail_fin() {
    // caudal peduncle keel
    translate([-body_len/2-6, 0, 0]) {
        rotate([90, 0, 0]) {
            difference() {
                union() {
                    // upper lobe
                    translate([0, 0, 1]) {
                        linear_extrude(height = 1) {
                            polygon(points = [
                                [0, 0],
                                [0, 15],
                                [-12, 12],
                                [-10, 0]
                            ]);
                        }
                    }
                    // lower lobe
                    translate([0, 0, -1]) {
                        linear_extrude(height = 1) {
                            polygon(points = [
                                [0, 0],
                                [0, -15],
                                [-12, -12],
                                [-10, 0]
                            ]);
                        }
                    }
                }
                // remove center overlap
                cube([20, 30, 4], center = true);
            }
        }
    }
}

module head_detail() {
    // mouth slit
    color("gray")
    translate([body_len/2-2, 0, -body_dia/4]) {
        rotate([0, 90, 0])
            rotate([15, 0, 0])
                cylinder(h = 0.5, d = 6);
    }

    // eye
    translate([body_len/2-5, body_dia/3, body_dia/6]) {
        sphere(d = 3);
        translate([0, 0, 1.1])
            sphere(d = 1.5);
    }
    translate([body_len/2-5, -body_dia/3, body_dia/6]) {
        sphere(d = 3);
        translate([0, 0, 1.1])
            sphere(d = 1.5);
    }
}

module tiny_fins() {
    // anal fin
    translate([-body_len*0.38, 0, -body_dia/2+0.5]) {
        rotate([-90, 0, 0]) {
            linear_extrude(height = 0.8) {
                polygon(points = [
                    [0, 0],
                    [8, 0],
                    [4, 7],
                    [1, 6],
                    [0, 2]
                ]);
            }
        }
    }

    // pelvic fins
    for(y = [body_dia/3, -body_dia/3]) {
        translate([body_len*0.1, y, -body_dia/2+0.5]) {
            rotate([0, 0, y > 0 ? -45 : 45])
                rotate([-90, 0, 0]) {
                    linear_extrude(height = 0.8) {
                        polygon(points = [
                            [0, 0],
                            [5, 0],
                            [3, 5],
                            [1, 4],
                            [0, 2]
                        ]);
                    }
                }
        }
    }
}

// assemble complete fish
union() {
    tuna_body();
    dorsal_fin();
    pectoral_fin();
    tail_fin();
    head_detail();
    tiny_fins();
}

