// Realistic Turtle - Medium Complexity
// Approx 80mm long, 3D-printable with no steep overhangs

// Shell segments module
module shell_segment(r, h, angle) {
    rotate([0, 0, angle]) 
        translate([r, 0, 0])
            scale([1.2, 1, 1])
                cylinder(h=h, r1=6, r2=3, $fn=6);
}

// Main shell
module turtle_shell() {
    difference() {
        // Outer shell dome
        scale([1.5, 1.2, 0.8])
            sphere(r=30, $fn=100);

        // Flatten bottom
        translate([0, 0, -30])
            cube([100, 100, 60], center=true);
    }

    // Shell pattern segments
    for(i=[0:30:359]) {
        shell_segment(20, 5, i);
        shell_segment(15, 4, i+15);
        shell_segment(10, 3, i+30);
    }

    // Shell rim
    rotate_extrude($fn=100)
        translate([28, 0, 0])
            circle(r=3);
}

// Head with snout
module turtle_head() {
    union() {
        // Main head sphere
        sphere(r=8, $fn=30);

        // Snout
        translate([9, 0, -2])
            rotate([0, 20, 0])
                scale([1.5, 1, 0.8])
                    cylinder(h=8, r1=5, r2=0, $fn=20);
    }
}

// Eye
module turtle_eye() {
    translate([3, -3.5, 2])
        sphere(r=1.5, $fn=20);
}

// Flipper with toes
module turtle_flipper(length, width, angle) {
    rotate([0, 0, angle])
        translate([0, length/2, 0])
            rotate([90, 0, 0])
                difference() {
                    // Main flipper shape
                    scale([width, length, width/2])
                        sphere(r=5, $fn=20);

                    // Flatten bottom
                    translate([0, 0, -5])
                        cube([20, 20, 10], center=true);
                };

    // Toes
    for(i=[-1:1]) {
        rotate([0, 0, angle])
            translate([i*3, length+2, -2])
                cylinder(h=4, r=1, $fn=8);
    }
}

// Tail
module turtle_tail() {
    rotate([90, 0, 0])
        cylinder(h=12, r1=6, r2=2, $fn=20);
}

// Assemble turtle
module turtle() {
    // Shell
    turtle_shell();

    // Head
    translate([35, 0, 0])
        turtle_head();

    // Eyes
    color("black") {
        translate([35, 0, 0]) {
            translate([7.5, -3.5, 2]) turtle_eye();
            translate([7.5, 3.5, 2]) turtle_eye();
        }
    }

    // Front flippers
    translate([15, 20, -5])
        turtle_flipper(25, 8, -30);
    translate([15, -20, -5])
        turtle_flipper(25, 8, 30);

    // Rear flippers
    translate([-25, 18, -5])
        turtle_flipper(20, 7, -45);
    translate([-25, -18, -5])
        turtle_flipper(20, 7, 45);

    // Tail
    translate([-38, 0, -5])
        turtle_tail();
}

// Render the turtle
turtle();

