// Turnstone (Arenaria interpres) - medium complexity realistic model
// Approximate length: 80mm

$fn = 32;  // Smooth curves

module body() {
    difference() {
        // Main body
        scale([1.2, 0.8, 0.6])
        sphere(r=15);

        // Flatten underside
        translate([0, 0, -10])
        cube([40, 30, 20], center=true);
    }
}

module head() {
    translate([16, 0, 3])
    rotate([0, -20, 0])
    scale([1.3, 1, 1])
    sphere(r=8);
}

module beak() {
    translate([23, 0, 3])
    rotate([0, -30, 0])
    scale([1, 0.3, 0.2])
    cylinder(h=12, r1=3, r2=0.5);
}

module eye() {
    translate([20, 4, 5])
    sphere(r=1.5);

    // Eye highlight
    translate([20.5, 4.3, 5.5])
    scale([1, 1, 0.5])
    sphere(r=0.5);
}

module wing() {
    translate([0, 8, 5])
    rotate([0, 0, 10])
    scale([1, 0.3, 0.5])
    sphere(r=12);

    // Wing pattern lines
    for(i = [1:3]) {
        translate([-5 + i*3, 11, 5])
        rotate([90, 0, 0])
        scale([0.2, 0.2, 6])
        cylinder(h=1, r1=1, r2=1);
    }
}

module tail() {
    translate([-15, 0, 2])
    rotate([0, 20, 0])
    scale([0.5, 0.8, 0.2])
    sphere(r=10);
}

module leg(pos) {
    translate(pos)
    cylinder(h=10, r=1.2);

    // Foot
    translate([pos[0], pos[1], pos[2]-10])
    union() {
        // Main foot
        scale([1.5, 0.3, 0.2])
        sphere(r=3);

        // Toes
        for(angle = [0:120:240]) {
            rotate([0, 0, angle])
            translate([3, 0, 0])
            scale([0.8, 0.2, 0.15])
            sphere(r=2);
        }
    }
}

module legs() {
    // Left leg
    leg([0, 7, -7]);

    // Right leg  
    leg([0, -7, -7]);
}

// Main assembly
union() {
    // Body parts
    body();
    head();
    beak();
    eye();
    wing();
    tail();

    // Mirror wing for other side
    mirror([0, 1, 0])
    wing();

    // Legs
    legs();
}

