// Realistic Vanga Bird - Medium Complexity OpenSCAD Model
// Body length ~80mm, wingspan ~60mm, height ~35mm

$fn = 32;

module vanga_body() {
    // Main body - elongated oval
    difference() {
        scale([1.3, 1, 1]) sphere(r=15);
        translate([0, 0, -15]) cube([35, 35, 30], center=true);
    }
}

module vanga_head() {
    // Head - slightly flattened sphere
    translate([-20, 0, 8]) {
        scale([1.2, 1, 0.9]) sphere(r=12);
    }
}

module vanga_beak() {
    // Hooked beak characteristic of vangas
    translate([-32, 0, 8]) {
        rotate([0, -20, 0]) {
            difference() {
                cylinder(h=18, r1=4, r2=2);
                translate([0, 0, 9]) rotate([0, 30, 0]) 
                    cube([15, 10, 10], center=true);
            }
        }
    }
}

module vanga_wing(side) {
    // Wing with primary feathers
    mirror([side, 0, 0]) {
        translate([5, side*20, 5]) {
            rotate([0, 0, side*15]) {
                difference() {
                    scale([1.5, 1, 0.2]) sphere(r=12);
                    // Wing tip cut
                    translate([18, side*8, 0]) rotate([0, 0, side*30])
                        cube([10, 8, 5], center=true);
                }

                // Primary feathers
                for(i = [0:2]) {
                    translate([12 + i*3, side*(8 + i*2), -1]) {
                        rotate([0, 0, side*(15 - i*5)])
                            scale([0.3, 1, 0.1]) cylinder(h=8, r=3);
                    }
                }
            }
        }
    }
}

module vanga_tail() {
    // Forked tail
    translate([18, 0, 0]) {
        rotate([0, 10, 0]) {
            for(side = [-1, 1]) {
                translate([0, side*5, -2]) {
                    rotate([0, 0, side*20]) {
                        scale([1.2, 0.3, 0.2]) cylinder(h=15, r=5);
                    }
                }
            }
        }
    }
}

module vanga_legs() {
    // Legs and feet
    for(side = [-1, 1]) {
        translate([-5, side*8, -15]) {
            // Leg
            cylinder(h=12, r=2);

            // Foot
            translate([0, 0, -12]) {
                rotate([0, 0, side*45]) {
                    for(i = [-1, 0, 1]) {
                        translate([i*3, 0, 0]) {
                            scale([0.8, 0.3, 0.3]) cube([4, 2, 1]);
                        }
                    }
                }
            }
        }
    }
}

module vanga_eye(side) {
    // Eye with pupil
    translate([-25, side*5, 12]) {
        color("black") sphere(r=2);
        translate([0.5, 0, 0.5]) color("white") sphere(r=0.8);
    }
}

// Assembly
union() {
    vanga_body();
    vanga_head();
    vanga_beak();
    vanga_wing(1);
    vanga_wing(-1);
    vanga_tail();
    vanga_legs();

    // Eyes
    vanga_eye(1);
    vanga_eye(-1);
}

// Base for stability
translate([0, 0, -20]) {
    color("darkgreen") cylinder(h=2, r=20);
}

