// Realistic Vampire Bust - Medium Complexity
// 3D printable without supports

// Overall scale
scale_factor = 1;
head_height = 60 * scale_factor;
head_width = 45 * scale_factor;

// Head module
module head() {
    difference() {
        // Main head shape
        sphere(d = head_width, $fn=50);

        // Neck cavity
        translate([0, 0, -head_height/3])
            cylinder(h = head_height/3, d = head_width/2, $fn=30);

        // Eye sockets
        translate([-head_width/4, head_width/3, head_width/6])
            sphere(d = head_width/5, $fn=20);
        translate([head_width/4, head_width/3, head_width/6])
            sphere(d = head_width/5, $fn=20);
    }
}

// Nose module
module nose() {
    translate([0, head_width/2.5, head_width/6])
        rotate([90, 0, 0])
            cylinder(h = head_width/4, d1 = head_width/8, d2 = head_width/12, $fn=20);
}

// Ears module
module ears() {
    // Left ear
    translate([-head_width/2, 0, 0])
        rotate([0, 90, 10])
            scale([1, 1.5, 1])
                difference() {
                    sphere(d = head_width/4, $fn=20);
                    translate([head_width/8, 0, 0])
                        sphere(d = head_width/4.5, $fn=20);
                }

    // Right ear
    translate([head_width/2, 0, 0])
        rotate([0, 90, -10])
            scale([1, 1.5, 1])
                mirror([1, 0, 0])
                    difference() {
                        sphere(d = head_width/4, $fn=20);
                        translate([head_width/8, 0, 0])
                            sphere(d = head_width/4.5, $fn=20);
                    }
}

// Hair module
module hair() {
    // Main hair volume
    translate([0, -head_width/4, head_width/3])
        scale([1.1, 1.2, 0.8])
            sphere(d = head_width, $fn=30);

    // Hair strands
    for(i = [-3:3]) {
        translate([i*head_width/8, -head_width/2, head_width/3])
            rotate([0, 30, 0])
                cylinder(h = head_width/3, d = head_width/20, $fn=8);
    }
}

// Fangs module
module fangs() {
    // Left fang
    translate([-head_width/8, head_width/2.2, -head_width/6])
        rotate([0, 0, -15])
            cylinder(h = head_width/8, d1 = head_width/30, d2 = head_width/50, $fn=8);

    // Right fang
    translate([head_width/8, head_width/2.2, -head_width/6])
        rotate([0, 0, 15])
            cylinder(h = head_width/8, d1 = head_width/30, d2 = head_width/50, $fn=8);
}

// Eyebrows module
module eyebrows() {
    // Left eyebrow
    translate([-head_width/4, head_width/3.2, head_width/4])
        rotate([20, 0, -20])
            scale([1, 0.3, 0.2])
                cube([head_width/6, head_width/20, head_width/20]);

    // Right eyebrow
    translate([head_width/4, head_width/3.2, head_width/4])
        rotate([20, 0, 20])
            scale([1, 0.3, 0.2])
                cube([head_width/6, head_width/20, head_width/20]);
}

// Cape collar
module cape_collar() {
    translate([0, 0, -head_height/3])
        rotate_extrude(angle=360, $fn=50)
            translate([head_width/2.2, 0])
                circle(d = head_width/10, $fn=20);
}

// Main assembly
union() {
    // Head assembly
    union() {
        head();
        nose();
        ears();
        hair();
        fangs();
        eyebrows();
    }

    // Cape collar
    cape_collar();
}

