// Realistic Wandering (snail) – medium complexity
// All parts printable without support; largest dimension ≈ 75 mm

$fn = 50;               // Global facet resolution
shell_th = 2;           // Minimum printable wall thickness
scale_factor = 1.0;     // Can be scaled later for desired print size

/* ---------- Shell (main body) ---------- */
module shell(h=35, d=55) {
    // Basic shell spiral created by subtracting successively smaller cylinders
    difference() {
        union() {
            // Outer dome
            translate([0, 0, h/2])
                scale([1, 1, 0.7])
                sphere(d = d);

            // Whorl risers for realistic ridges
            for (a = [0 : 30 : 330]) {
                rotate([0, 0, a])
                    translate([d/2 - 4, 0, h/2 - 8])
                        rotate([90, 0, 0])
                            cylinder(h = 8, d1 = 6, d2 = 4, $fn = 6);
            }
        }

        // Hollow interior
        translate([0, 0, h/2 - shell_th])
            scale([1, 1, 0.6])
            sphere(d = d - 2*shell_th);

        // Spiral cavity
        for (i = [1 : 3]) {
            rotate([0, 0, i*120])
                translate([i*4, 0, h/2 - 5])
                    rotate([0, -90, 0])
                        cylinder(h = d/2, d = d/2 - i*6, $fn = 20);
        }
    }
}

/* ---------- Foot / Body ---------- */
module foot() {
    // Main muscular foot
    translate([0, -25, 0])
        scale([1, 2.5, 0.4])
        sphere(d = 30);

    // Underside flattening for print bed contact
    translate([0, -25, -2])
        cube([30, 50, 4], center = true);
}

/* ---------- Tentacles & Eyes ---------- */
module tentacle(len = 12, d = 3, angle = 0) {
    translate([0, 0, len/2])
        rotate([angle, 0, 0])
            cylinder(h = len, d = d);

    // Eyeball at tip
    translate([0, 0, len])
        sphere(d = d + 1);
}

module head_detail() {
    // Head mass
    translate([0, -38, 5])
        sphere(d = 12);

    // Left tentacle
    translate([-5, -42, 8])
        tentacle(14, 2.5, -20);

    // Right tentacle
    translate([5, -42, 8])
        tentacle(14, 2.5, -20);
}

/* ---------- Final Assembly ---------- */
union() {
    color("BurlyWood") shell();
    color("AntiqueWhite") foot();
    color("RosyBrown") head_detail();
}

