        // Loom structure
        color(loom_color) loom();

        // Weaver figure
        color(weaver_color) weaver();
    }
}

// Loom frame
module loom() {
    // Base frame
    cube([60, 30, 4], center = true);

    // Vertical posts
    translate([-25, 0, 18]) cylinder(h = 36, d = 4, center = true);
    translate([25, 0, 18]) cylinder(h = 36, d = 4, center = true);

    // Top beam
    translate([0, 0, 36]) cube([54, 3, 3], center = true);

    // Warp threads (vertical strings)
    for (x = [-20:5:20]) {
        translate([x, -15, 2]) cylinder(h = 70, d = 0.6, center = false);
    }

    // Shuttle / heddle bar
    translate([0, 0, 20]) cube([50, 2, 1], center = true);
}

// Weaver figure
module weaver() {
    translate([0, 0, 2]) { // Position on loom seat
        union() {
            // Torso
            translate([0, 0, 22]) {
                cube([12, 8, 18], center = true);
            }

            // Head
            translate([0, 0, 34]) {
                sphere(d = 10);
            }

            // Hair bun
            color(hair_color) translate([0, 0, 38]) {
                sphere(d = 8);
            }

            // Arms
            left_arm();
            right_arm();

            // Skirt
            color(skirt_color) translate([0, 0, 12]) {
                cylinder(h = 10, d1 = 14, d2 = 12, center = false);
            }

            // Legs (seated)
            left_leg();
            right_leg();

            // Feet
            left_foot();
            right_foot();
        }
    }
}

// Left arm
module left_arm() {
    translate([-8, 0, 25]) {
        rotate([0, 30, -20]) {
            cylinder(h = 12, d = 3, center = false);
            translate([0, 0, 12]) rotate([20, 0, 0]) {
                cylinder(h = 8, d = 2.5, center = false);
            }
        }
    }
}

// Right arm
module right_arm() {
    translate([8, 0, 25]) {
        rotate([0, 30, 20]) {
            cylinder(h = 12, d = 3, center = false);
            translate([0, 0, 12]) rotate([20, 0, 0]) {
                cylinder(h = 8, d = 2.5, center = false);
            }
        }
    }
}

// Left leg
module left_leg() {
    translate([-4, 0, 7]) {
        rotate([30, 0, 0]) {
            cylinder(h = 10, d = 4, center = false);
