// Waterfowl (Mallard Duck) - Realistic Medium Complexity
// Size: ~70mm length, 3D printable

// Parameters
body_length = 35;
body_width = 20;
body_height = 18;
neck_length = 12;
neck_diameter = 6;
head_radius = 8;
beak_length = 10;
beak_width = 3;
wing_span = 30;
tail_length = 12;
leg_length = 15;
foot_size = 8;
wall_thickness = 2;

// Body with gentle curves
module duck_body() {
    difference() {
        // Main body ellipsoid
        scale([1, 0.7, 0.6]) 
        sphere(d=body_length);

        // Flatten bottom for printing
        translate([0, 0, -body_height/2 - 1])
            cube([body_length*2, body_width*2, 2], center=true);

        // Hollow interior
        scale([0.8, 0.55, 0.45]) 
        sphere(d=body_length - wall_thickness*2);
    }
}

// Neck connecting body to head
module neck() {
    translate([body_length/2 - 5, 0, body_height/2 - 2])
        rotate([0, -20, 0])
        cylinder(h=neck_length, d1=neck_diameter, d2=neck_diameter*0.8);
}

// Head with integrated beak
module head_and_beak() {
    translate([body_length/2 + neck_length - 2, 0, body_height/2 + neck_length/2 - 3]) {
        // Head sphere
        sphere(r=head_radius);

        // Beak - slightly curved
        translate([head_radius + beak_length/2 - 2, 0, -head_radius/3])
            rotate([0, -10, 0])
            scale([1, 0.5, 0.3])
            sphere(d=beak_length);

        // Eye indentations
        for(side = [-1, 1]) {
            translate([head_radius/2, side*head_radius/2, head_radius/3])
                sphere(d=2);
        }
    }
}

// Wing with feather texture using loops
module wing() {
    module wing_half(side) {
        translate([body_length/4, side*body_width/2, body_height/3]) {
            rotate([0, 0, side*20])
            difference() {
                // Main wing shape
                scale([1.2, 0.2, 0.8])
                sphere(d=wing_span/2);

                // Feather cuts
                for(i = [1:3]) {
                    rotate([0, 0, side*i*15])
                    translate([i*3, 0, 0])
                        cube([wing_span/4, wing_span/2, wing_span/4], center=true);
                }
            }
        }
    }

    wing_half(1);
    wing_half(-1);
}

// Tail with individual feathers
module tail() {
    translate([-body_length/2 - tail_length/2 + 5, 0, body_height/4]) {
        rotate([0, 20, 0])
        for(i = [0:3]) {
            rotate([0, 0, i*8 - 12])
            translate([0, i*2 - 3, 0])
                scale([1, 0.3, 0.1])
                sphere(d=tail_length);
        }
    }
}

// Leg and foot
module leg(side) {
    translate([0, side*body_width/3, -body_height/2]) {
        // Upper leg
        cylinder(h=leg_length/2, d=3);

        // Lower leg
        translate([0, 0, -leg_length/2])
            rotate([15, 0, 0])
            cylinder(h=leg_length/2, d=2.5);

        // Webbed foot
        translate([2, 0, -leg_length - 1])
            rotate([0, 90, 0])
            difference() {
                // Main foot
                scale([1.5, 1, 0.3])
                sphere(d=foot_size);

                // Web cuts
                for(i = [0:2]) {
                    translate([0, i*3 - 3, 0])
                        cube([foot_size, 1, foot_size], center=true);
                }
            }
    }
}

// Assembly
union() {
    duck_body();
    neck();
    head_and_beak();
    wing();
    tail();
    leg(1);
    leg(-1);
}

