// Realistic Weevil - Medium Complexity
// 3D printable model with proper wall thickness and no overhangs

$fn = 32;

// Overall dimensions (mm)
body_length = 60;
body_height = 25;
head_length = 15;

// Main body segments
module weevil_body() {
    // Thorax (main body)
    union() {
        // Central body segment - ellipsoid shape
        scale([1.2, 1, 0.8]) 
            sphere(d=body_length * 0.6);

        // Abdomen - tapered rear
        translate([body_length * 0.3, 0, -body_height * 0.2])
            scale([1.5, 1.2, 0.6])
                sphere(d=body_length * 0.5);

        // Prothorax - front segment
        translate([-body_length * 0.2, 0, 0])
            scale([0.8, 0.9, 0.7])
                sphere(d=body_length * 0.4);
    }
}

// Head with snout
module weevil_head() {
    union() {
        // Main head capsule
        translate([-body_length * 0.4, 0, body_height * 0.3])
            rotate([0, -10, 0])
            scale([0.8, 0.9, 0.7])
                sphere(d=head_length);

        // Snout (rostrum) - curved downward
        translate([-body_length * 0.5, 0, body_height * 0.2])
            rotate([0, -20, 0])
            scale([1, 0.6, 0.6])
                cylinder(h=head_length * 1.2, d1=head_length * 0.8, d2=head_length * 0.3);

        // Antennae bases
        for(side = [-1, 1]) {
            translate([-body_length * 0.45, side * head_length * 0.3, body_height * 0.4])
                rotate([0, 0, side * 30])
                cylinder(h=head_length * 0.3, d=head_length * 0.15);

            // Antenna segments
            translate([-body_length * 0.45, side * head_length * 0.3, body_height * 0.4])
                rotate([0, 0, side * 30])
                translate([head_length * 0.25, 0, 0])
                cylinder(h=head_length * 0.4, d=head_length * 0.08);
        }
    }
}

// Elytra (wing covers)
module elytra() {
    union() {
        for(side = [-1, 1]) {
            translate([body_length * 0.1, side * body_length * 0.15, body_height * 0.1])
                rotate([0, 0, side * 10])
                scale([1.3, 0.4, 0.3])
                    difference() {
                        sphere(d=body_length * 0.5);
                        // Cut along center line
                        translate([0, -side * 10, 0])
                            cube([body_length, body_length, body_length], center=true);
                    }
        }
    }
}

// Legs - six legs with jointed segments
module legs() {
    // Front legs
    for(side = [-1, 1]) {
        for(pos = [0, 1]) {
            translate([-body_length * 0.25 + pos * body_length * 0.15, 
                       side * body_length * 0.22, 
                       -body_height * 0.2]) {
                // Coxa
                rotate([45, 0, 0])
                    cylinder(h=body_length * 0.08, d=body_length * 0.06);

                // Femur
                translate([0, 0, body_length * 0.08])
                    rotate([90, 0, 0])
                    cylinder(h=body_length * 0.12, d=body_length * 0.05);

                // Tibia
                translate([0, 0, body_length * 0.08 + body_length * 0.12])
                    rotate([90, 0, 0])
                    cylinder(h=body_length * 0.1, d=body_length * 0.04);

                // Tarsus
                translate([0, 0, body_length * 0.08 + body_length * 0.12 + body_length * 0.1])
                    rotate([90, 0, 0])
                    scale([1, 1, 0.8])
                    cylinder(h=body_length * 0.08, d=body_length * 0.03);
            }
        }
    }

    // Middle legs
    for(side = [-1, 1]) {
        translate([body_length * 0.05, side * body_length * 0.25, -body_height * 0.2]) {
            rotate([30, 0, 0])
                cylinder(h=body_length * 0.08, d=body_length * 0.06);

            translate([0, 0, body_length * 0.08])
                rotate([90, 0, 0])
                cylinder(h=body_length * 0.13, d=body_length * 0.05);

            translate([0, 0, body_length * 0.08 + body_length * 0.13])
                rotate([90, 0, 0])
                cylinder(h=body_length * 0.11, d=body_length * 0.04);

            translate([0, 0, body_length * 0.08 + body_length * 0.13 + body_length * 0.11])
                rotate([90, 0, 0])
                scale([1, 1, 0.8])
                cylinder(h=body_length * 0.09, d=body_length * 0.03);
        }
    }
}

// Compound eyes
module eyes() {
    for(side = [-1, 1]) {
        translate([-body_length * 0.42, side * head_length * 0.35, body_height * 0.35])
            scale([0.8, 1, 0.8])
            sphere(d=head_length * 0.3);
    }
}

// Surface texture - simple grooves
module surface_texture() {
    // Grooves on elytra
    for(i = [0:5]) {
        translate([body_length * 0.2 + i * body_length * 0.05, 0, body_height * 0.15])
            scale([1, 0.5, 0.02])
            cube([body_length * 0.02, body_length * 0.3, body_length * 0.3], center=true);
    }
}

// Assemble complete weevil
union() {
    weevil_body();
    weevil_head();
    elytra();
    legs();
    eyes();
    surface_texture();
}

