    translate([x_offset, 0, z_offset])
    rotate([0, 0, angle])
    union() {
        // Upper leg
        translate([0, 0, 5])
        scale([1.2, 1.2, 2.5])
        sphere(5, $fn=12);

        // Lower leg
        translate([0, 0, -5])
        cylinder(h=15, r1=4, r2=3, $fn=8);

        // Hoof
        translate([0, 0, -13])
        scale([1.2, 1.2, 0.8])
        cylinder(h=5, r=3.5, $fn=8);
    }
}

module tusk(side) {
    translate([side * 12, 5, 0])
    rotate([0, -20, side * 10])
    scale([1, 1, 2.5])
    cylinder(h=15, r1=2.5, r2=0.5, $fn=6);
}

module ear(side) {
    translate([side * 8, 12, 8])
    rotate([0, -10, side * 15])
    scale([0.8, 1.5, 2])
    sphere(5, $fn=12);
}

module tail() {
    translate([0, -25, 8])
    rotate([20, 0, 0])
    union() {
        cylinder(h=20, r1=2.5, r2=1, $fn=8);
        translate([0, 0, 20])
        sphere(2.5, $fn=8);
    }
}

// Main body
union() {
    // Body
    translate([0, 0, 12])
    scale([1.8, 2.2, 1.3])
    sphere(15, $fn=20);

    // Head
    translate([0, 25, 15])
    rotate([-10, 0, 0])
    scale([1.2, 1.5, 1.1])
