// Realistic Whale Model - Medium Complexity
// Designed for 3D printing with no overhangs

module whale_body() {
    // Main body - elongated ellipsoid
    scale([1.5, 1, 0.8])
    sphere(r=20, $fn=50);
}

module whale_head() {
    // Head section - slightly flattened sphere
    translate([-25, 0, 0])
    scale([1.2, 1, 0.7])
    sphere(r=15, $fn=40);
}

module whale_tail() {
    // Tail base
    translate([35, 0, 0])
    rotate([0, 90, 0])
    cylinder(h=15, r1=8, r2=0, $fn=30);

    // Tail flukes - two flattened ellipsoids
    translate([42, 0, 0])
    rotate([0, 90, 0])
    scale([2, 0.2, 1])
    sphere(r=8, $fn=20);

    translate([42, 0, 0])
    rotate([0, 90, 0])
    rotate([0, 0, 90])
    scale([2, 0.2, 1])
    sphere(r=8, $fn=20);
}

module blowhole() {
    // Two small cylinders for blowholes
    for(i = [-1, 1]) {
        translate([0, i*3, 15])
        cylinder(h=3, r=1.5, $fn=12);
    }
}

