// Whistler (Train Whistle) - Realistic Style, Medium Complexity

// Overall dimensions
whistle_length = 85;
whistle_width = 25;
whistle_height = 30;
wall_thickness = 2;

// Main body with rounded edges
module whistle_body() {
    difference() {
        // Main block with rounded top
        hull() {
            // Bottom corners
            translate([wall_thickness, wall_thickness, 0])
                cylinder(h=whistle_height-wall_thickness, r=wall_thickness, $fn=16);
            translate([whistle_length-wall_thickness, wall_thickness, 0])
                cylinder(h=whistle_height-wall_thickness, r=wall_thickness, $fn=16);
            translate([wall_thickness, whistle_width-wall_thickness, 0])
                cylinder(h=whistle_height-wall_thickness, r=wall_thickness, $fn=16);
            translate([whistle_length-wall_thickness, whistle_width-wall_thickness, 0])
                cylinder(h=whistle_height-wall_thickness, r=wall_thickness, $fn=16);

            // Top corners
            translate([wall_thickness, wall_thickness, whistle_height-wall_thickness])
                sphere(r=wall_thickness, $fn=16);
            translate([whistle_length-wall_thickness, wall_thickness, whistle_height-wall_thickness])
                sphere(r=wall_thickness, $fn=16);
            translate([wall_thickness, whistle_width-wall_thickness, whistle_height-wall_thickness])
                sphere(r=wall_thickness, $fn=16);
            translate([whistle_length-wall_thickness, whistle_width-wall_thickness, whistle_height-wall_thickness])
                sphere(r=wall_thickness, $fn=16);
        }

        // Hollow out interior
        translate([wall_thickness, wall_thickness, wall_thickness])
            cube([whistle_length-2*wall_thickness, whistle_width-2*wall_thickness, whistle_height-2*wall_thickness]);
    }
}

// Mouthpiece with curved entry
module mouthpiece() {
    difference() {
        // Curved mouthpiece
        hull() {
            cylinder(h=15, r=whistle_width/2, $fn=32);
            translate([0, 0, 15])
                cylinder(h=10, r=whistle_width/3, $fn=32);
        }

        // Air channel
        translate([0, 0, -1])
            cylinder(h=27, r=whistle_width/4, $fn=32);

        // Entry bevel
        rotate([0, 45, 0])
            translate([-whistle_width/2-5, 0, -whistle_width/2])
                cube([whistle_width+10, whistle_width, whistle_width+10]);
    }
}

// Sound holes with chamfers
module sound_holes() {
    // Parameters for 4 holes
    hole_spacing = (whistle_length - 40) / 3;
    hole_diameter = 6;
    hole_depth = 10;

    for(i = [0:3]) {
        translate([35 + i * hole_spacing, whistle_width/2, whistle_height]) {
            // Chamfered edge
            cylinder(h=2, r1=hole_diameter/2 + 1, r2=hole_diameter/2, $fn=32);

            // Main hole
            translate([0, 0, 2])
                cylinder(h=hole_depth, r=hole_diameter/2, $fn=32);

            // Exit hole
            translate([0, 0, 2+hole_depth])
                cylinder(h=1, r=hole_diameter/2 + 0.5, $fn=32);
        }
    }
}

// Air splitter wedge
module air_splitter() {
    translate([25, whistle_width/2 - 1, wall_thickness*2]) {
        rotate([0, 90, 0])
            cylinder(h=2, r1=0, r2=whistle_height/3, $fn=3);
    }
}

// Assembly
union() {
    whistle_body();

    // Mouthpiece at end
    translate([0, whistle_width/2, whistle_height/2 - 12])
        rotate([0, 90, 0])
            mouthpiece();

    // Sound holes on top
    sound_holes();

    // Air splitter inside
    air_splitter();

    // Decorative ridges
    for(ridge = [1:3]) {
        translate([ridge * 20, 0, whistle_height - 3])
            cube([2, whistle_width, 1]);
    }

    // Hanging loop
    translate([whistle_length/2, whistle_width, whistle_height])
        union() {
            rotate([90, 0, 0])
                cylinder(h=10, r=2, $fn=16);
            translate([0, 0, 10])
                sphere(r=2, $fn=16);
        }
}

