// Whydah bird - realistic medium complexity
// Overall dimensions: ~75mm length

// Main body
module body() {
    union() {
        // Central torso
        translate([0, 0, 5]) 
            scale([1.2, 0.8, 1])
            sphere(d=20);

        // Chest
        translate([8, 0, 0])
            scale([1.5, 1, 1.2])
            sphere(d=15);

        // Abdomen
        translate([-8, 0, -2])
            scale([0.8, 0.7, 1])
            sphere(d=16);
    }
}

// Head with beak and crest
module head() {
    union() {
        // Main head sphere
        translate([15, 0, 15])
            sphere(d=12);

        // Beak
        translate([20, 0, 15])
            rotate([0, -10, 0])
            scale([2, 0.3, 0.3])
            cylinder(h=8, d1=3, d2=0);

        // Crest feathers
        for(i = [0:3]) {
            translate([12, -3 + i*2, 20])
                rotate([0, 10 - i*5, 0])
                scale([0.2, 1, 2])
                cylinder(h=6, d=2);
        }
    }
}

// Wing module
module wing(side) {
    mirror([side, 0, 0]) {
        // Primary wing shape
        translate([0, -15, 5])
            rotate([0, 0, -20])
            scale([1, 2.5, 0.3])
            cylinder(h=25, d1=8, d2=3);

        // Secondary feathers
        for(i = [0:4]) {
            translate([-2 - i*3, -20 - i*2, 3])
                rotate([0, 0, -30 + i*5])
                scale([0.8 - i*0.1, 1.2, 0.2])
                cylinder(h=10 - i, d=6 - i);
        }
    }
}

// Tail with long streamers
module tail() {
    union() {
        // Tail base
        translate([-15, 0, 0])
            scale([1, 0.5, 0.3])
            cylinder(h=10, d=8);

        // Central tail feathers
        for(i = [0:2]) {
            translate([-20, -3 + i*3, 0])
                rotate([0, 0, -10 + i*10])
                cylinder(h=30, d1=3, d2=1);
        }

        // Long streamers (male characteristic)
        translate([-22, -2, 0])
            rotate([0, 0, -15])
            cylinder(h=45, d1=2, d2=0.5);

        translate([-22, 2, 0])
            rotate([0, 0, 15])
            cylinder(h=45, d1=2, d2=0.5);
    }
}

// Legs and feet
module legs() {
    for(side = [-1, 1]) {
        translate([0, side*3, -5]) {
            // Upper leg
            translate([0, 0, 0])
                rotate([20, 0, 0])
                cylinder(h=8, d=2);

            // Lower leg
            translate([0, 0, -9])
                rotate([30, 0, 0])
                cylinder(h=10, d=1.5);

            // Foot
            translate([0, side*2, -19])
                rotate([0, 90, 0])
                scale([1, 0.3, 0.3])
                cylinder(h=6, d=2);

            // Toes
            for(i = [0:2]) {
                translate([2 + i*2, side*(2 + i), -19])
                    rotate([0, 90, -10 + i*10])
                    scale([0.7, 0.3, 0.3])
                    cylinder(h=4, d=1);
            }
        }
    }
}

// Eyes
module eyes() {
    for(side = [-1, 1]) {
        translate([17.5, side*3, 16])
            sphere(d=2);

        // Pupil
        translate([18, side*3.2, 16])
            sphere(d=1);
    }
}

// Assemble the bird
union() {
    body();
    head();
    eyes();
    wing(1);  // Right wing
    wing(-1); // Left wing
    tail();
    legs();
}

