// Wheat ear - realistic style, medium complexity
// Designed for 3D printing with no overhangs

$fn = 20; // Resolution for curved surfaces

// Main wheat ear module
module wheat_ear() {
    // Central stem
    color("goldenrod")
    stem();

    // Spikelets along the ear
    spikelets();

    // Awns (bristles)
    awns();
}

// Create the main stem
module stem() {
    // Bottom thick part
    cylinder(h = 30, r1 = 2, r2 = 1.5, center = false);

    // Middle section
    translate([0, 0, 30])
    cylinder(h = 25, r1 = 1.5, r2 = 1.2, center = false);

    // Top tapering section
    translate([0, 0, 55])
    cylinder(h = 20, r1 = 1.2, r2 = 0.8, center = false);
}

// Create spikelets (grain clusters)
module spikelets() {
    // Parameters for positioning
    total_spikelets = 20;
    ear_length = 75;

    for (i = [0:total_spikelets-1]) {
        // Calculate position along the ear
        height = (i * ear_length) / (total_spikelets - 1);
        radius = max(0.3, 2 - (height / 40)); // Tapering towards top

        // Alternate left and right
        angle = (i % 2 == 0) ? 30 : -30;

        translate([0, 0, height])
        rotate([0, 0, angle])
        translate([radius, 0, 0])
        single_spikelet();
    }
}

// Individual spikelet
module single_spikelet() {
    // Main grain body
    color("wheat")
    union() {
        // Central grain
        sphere(r = 1.2);

        // Side grains
        translate([0.8, 0, 0.3])
        rotate([0, 20, 0])
        sphere(r = 0.9);

        translate([-0.8, 0, 0.3])
        rotate([0, -20, 0])
        sphere(r = 0.9);

        translate([0, 0.8, 0.3])
        rotate([20, 0, 0])
        sphere(r = 0.8);

        translate([0, -0.8, 0.3])
        rotate([-20, 0, 0])
        sphere(r = 0.8);
    }
}

// Create awns (bristles)
module awns() {
    total_awns = 30;
    ear_length = 75;

    for (i = [0:total_awns-1]) {
        height = (i * ear_length) / (total_awns - 1);
        radius = max(0.3, 2 - (height / 40));

        // Distribute awns around the stem
        angle = (i * 137.5) % 360; // Golden angle for natural distribution

        translate([0, 0, height])
        rotate([0, 0, angle])
        translate([radius, 0, 0])
        rotate([0, 20, 0])
        single_awn();
    }
}

// Single awn (bristle)
module single_awn() {
    // Tapered bristle
    color("goldenrod")
    cylinder(h = 15, r1 = 0.15, r2 = 0.05, center = false);
}

// Create the wheat ear
wheat_ear();

