// Wildcat - Realistic Style, Medium Complexity
// Designed for 3D printing with no overhangs
// Approximate size: 80mm long x 35mm high

// Overall scale factor
scale_factor = 0.8;

// Basic dimensions (mm)
body_length = 60;
body_height = 25;
body_width = 20;

// Position the entire model
translate([0, 0, 0]) scale(scale_factor) {

    // Main body
    module body() {
        // Torso - ellipsoid shape
        translate([0, 0, body_height/2]) 
            scale([1, 0.8, 0.6])
            sphere(d=body_width);

        // Rear hip
        translate([-body_length/3, 0, 0])
            rotate([0, 30, 0])
            scale([1.2, 1, 0.8])
            sphere(d=body_width);

        // Chest
        translate([body_length/3, 0, 0])
            scale([1.1, 0.9, 0.9])
            sphere(d=body_width*1.1);
    }

    // Head with ears
    module head() {
        // Main head shape
        translate([body_length/2 + 8, 0, body_height + 5])
            scale([1.2, 1, 0.9])
            sphere(d=18);

        // Snout
        translate([body_length/2 + 15, 0, body_height + 3])
            scale([1.5, 0.6, 0.6])
            sphere(d=8);

        // Ears - triangular prisms
        for(side = [-1, 1]) {
            translate([body_length/2 + 4, side*7, body_height + 12])
                rotate([0, side*10, 0])
                cylinder(h=8, r1=3, r2=0.5);
        }

        // Eyes
        for(side = [-1, 1]) {
            translate([body_length/2 + 10, side*5, body_height + 8])
                sphere(d=3);
        }

        // Nose
        translate([body_length/2 + 18, 0, body_height + 3])
            sphere(d=2);
    }

    // Legs - simple cylinders
    module legs() {
        positions = [
            [body_length/3, body_width/2, 0],   // Front right
            [body_length/3, -body_width/2, 0],  // Front left
            [-body_length/3, body_width/2, 0],  // Rear right
            [-body_length/3, -body_width/2, 0]  // Rear left
        ];

        for(pos = positions) {
            translate(pos)
                rotate([-90, 0, 0])
                cylinder(h=15, r=3);

            // Paw
            translate([pos[0], pos[1], -15])
                sphere(d=6);
        }
    }

    // Tail
    module tail() {
        hull() {
            translate([-body_length/2 - 5, 0, body_height/2])
                sphere(d=6);

            translate([-body_length/2 - 25, 0, body_height/2 + 5])
                rotate([0, -30, 0])
                sphere(d=4);

            translate([-body_length/2 - 35, 0, body_height/2 + 8])
                rotate([0, -45, 0])
                sphere(d=2);
        }
    }

    // Combine all parts
    union() {
        body();
        head();
        legs();
        tail();
    }

    // Cut underside flat for printing
    translate([0, 0, -15])
        cube([100, 100, 15], center=true);
}

