// Whimbrel - medium complexity realistic style

// Overall size control
scale_factor = 1;  // Adjustable for different print sizes
body_length = 50 * scale_factor;
body_height = 25 * scale_factor;
head_size = 12 * scale_factor;
beak_length = 35 * scale_factor;
leg_length = 20 * scale_factor;

// Main body assembly
module whimbrel() {
    // Body - elongated oval
    translate([0, 0, leg_length + body_height/2])
    scale([1.5, 1, 1])
    sphere(d=body_height);

    // Neck connection
    translate([body_length/3, 0, leg_length + body_height*0.8])
    rotate([30, 0, 0])
    cylinder(h=head_size, d1=body_height*0.7, d2=head_size*0.8);

    // Head
    translate([body_length/2 + head_size, 0, leg_length + body_height*1.2])
    sphere(d=head_size);

    // Beak - curved downward
    translate([body_length/2 + head_size + beak_length/2, 0, leg_length + body_height*1.1])
    rotate([0, 0, -20])
    scale([1, 0.3, 0.3])
    cylinder(h=beak_length, d=head_size*0.8);

    // Beak tip
    translate([body_length/2 + head_size + beak_length, 0, leg_length + body_height*1.0])
    sphere(d=head_size*0.3);

    // Eye
    translate([body_length/2 + head_size*0.8, head_size*0.4, leg_length + body_height*1.3])
    sphere(d=head_size*0.15);

    // Wings - left and right
    for(side = [-1, 1]) {
        translate([0, side*body_height*0.6, leg_length + body_height*0.8])
        rotate([0, 0, side*20])
        scale([1.2, 0.3, 0.6])
        sphere(d=body_height*1.2);
    }

    // Tail
    translate([-body_length/2, 0, leg_length + body_height*0.7])
    rotate([20, 0, 0])
    scale([0.8, 0.3, 0.4])
    cylinder(h=body_height*0.8, d=body_height*0.8);

    // Legs
    for(side = [-1, 1]) {
        // Upper leg
        translate([body_length/4, side*body_height*0.3, leg_length*0.7])
        rotate([15, 0, side*10])
        cylinder(h=leg_length*0.5, d=body_height*0.15);

        // Lower leg
        translate([body_length/4 + 5*scale_factor, side*body_height*0.4, leg_length*0.3])
        rotate([0, 0, side*5])
        cylinder(h=leg_length*0.5, d=body_height*0.12);

        // Feet
        translate([body_length/4 + 8*scale_factor, side*body_height*0.45, leg_length*0.1])
        rotate([0, 90, 0])
        scale([1.5, 0.3, 1])
        sphere(d=body_height*0.25);

        // Toes
        for(toe = [-1, 0, 1]) {
            translate([body_length/4 + 10*scale_factor, side*body_height*0.45 + toe*body_height*0.15, leg_length*0.05])
            rotate([0, 90, toe*20])
            cylinder(h=body_height*0.15, d=body_height*0.08);
        }
    }

    // Feather texture - simple ridges
    for(i = [0:5]) {
        translate([-body_length/3 + i*body_length/6, 0, leg_length + body_height*0.3 + i*body_height/8])
        scale([1.2, 0.9, 0.1])
        sphere(d=body_height*0.9);
    }
}

