$fn = 32;

// Main module to create the woodcock
module woodcock() {
    // Rotate to standing position
    rotate([0, 0, 0]) {
        // Body
        union() {
            body();
            head();
            beak();
            wings();
            tail();
            legs();
            feet();
        }
    }
}

// Main body
module body() {
    color("brown") {
