// Wildebeest - medium complexity, realistic style
// Overall length ~80 mm

// Body parameters
body_len = 45;
body_h = 25;
body_w = 18;

// Head parameters
head_len = 15;
head_h = 12;
head_w = 9;

// Leg parameters
leg_h = 28;
leg_r = 3;

// Horn parameters
horn_len = 10;
horn_r = 1.5;

// Tail parameters
tail_len = 12;
tail_r = 1;

module torso() {
    // Main body
    union() {
        // Chest and abdomen
        hull() {
            translate([-body_len/2, 0, 0])
                scale([1.2, 1.1, 0.9])
                sphere(d = body_w);
            translate([body_len/2, 0, 0])
                scale([0.8, 0.9, 0.7])
                sphere(d = body_w);
        }

        // Back hump
        translate([body_len/4, 0, body_h/2])
            scale([1, 1.2, 0.8])
            sphere(d = body_w * 0.9);

        // Underbelly
        translate([0, 0, -body_h/2 + 2])
            scale([1.1, 1, 0.4])
            sphere(d = body_w);
