}

module head() {
    // Main head shape
    union() {
        // Skull
        translate([body_len/2 + head_len/2, 0, body_h/2 - 2])
            scale([1.3, 1, 1.1])
            sphere(d = head_w);

        // Muzzle
        translate([body_len/2 + head_len, 0, body_h/2 - 4])
            scale([1.5, 0.8, 0.6])
            sphere(d = head_w * 0.8);

        // Neck
        hull() {
            translate([body_len/2, 0, body_h/2 - 3])
                sphere(d = head_w * 1.2);
            translate([body_len/2 + head_len/2, 0, body_h/2 - 2])
                sphere(d = head_w * 0.9);
        }
    }
}

module horns() {
    // Left horn
    translate([body_len/2 + head_len, -head_w/3, body_h/2 + 2])
        rotate([0, 30, -15])
        cylinder(h = horn_len, r1 = horn_r, r2 = 0.5);

    // Right horn
    translate([body_len/2 + head_len, head_w/3, body_h/2 + 2])
        rotate([0, 30, 15])
        cylinder(h = horn_len, r1 = horn_r, r2 = 0.5);

    // Horn bases
    for (i = [-1, 1]) {
        translate([body_len/2 + head_len - 2, i * head_w/3, body_h/2])
            sphere(d = horn_r * 3);
    }
}

module legs() {
    // Generate all four legs
    for (side = [-1, 1]) {
        // Front leg
        translate([body_len/3 - 5, side * (body_w/2 + leg_r), -body_h/2])
            rotate([5, 0, 0])
            cylinder(h = leg_h, r = leg_r);

        // Back leg
        translate([-body_len/3 + 5, side * (body_w/2 + leg_r), -body_h/2])
            rotate([5, 0, 0])
            cylinder(h = leg_h, r = leg_r);

        // Hooves
        translate([body_len/3 - 5, side * (body_w/2 + leg_r), -body_h/2 - leg_h])
            cylinder(h = 2, r1 = leg_r, r2 = leg_r * 0.7);
        translate([-body_len/3 + 5, side * (body_w/2 + leg_r), -body_h/2 - leg_h])
            cylinder(h = 2, r1 = leg_r, r2 = leg_r * 0.7);
    }
}

module tail() {
    // Tail base
    translate([-body_len/2, 0, -body_h/2 + 5])
        sphere(d = tail_r * 3);

    // Tail tuft
    translate([-body_len/2 - tail_len, 0, -body_h/2 + 5])
        sphere(d = tail_r * 4);

    // Tail connection
    hull() {
        translate([-body_len/2, 0, -body_h/2 + 5])
            sphere(d = tail_r * 2);
        translate([-body_len/2 - tail_len, 0, -body_h/2 + 5])
            sphere(d = tail_r * 3);
    }
}

module ears() {
    // Left ear
    translate([body_len/2 + head_len - 2, -head_w/2, body_h/2 + 4])
        rotate([0, 20, -20])
        scale([0.5, 1, 2])
        sphere(d = head_w * 0.4);

    // Right ear
    translate([body_len/2 + head_len - 2, head_w/2, body_h/2 + 4])
        rotate([0, 20, 20])
        scale([0.5, 1, 2])
        sphere(d = head_w * 0.4);
}

// Assemble the wildebeest
union() {
    torso();
    head();
    horns();
    legs();
    tail();
    ears();
}

