// Medium-complexity realistic woodpecker for 3D printing
// Overall size ≈ 70 mm tall, 55 mm deep, 35 mm wide
// All parts joined with unions, no floating parts, ≥1 mm walls

$fn=24; // moderate facet count for printability

module woodpecker() {
    // Body core – oval, 35 mm long, 25 mm high, 20 mm thick
    difference() {
        scale([1, 0.7, 1]) sphere([17.5, 17.5, 10]);
        // Tail notch for tail insertion
        translate([15,0,0]) cube([8,8,20], center=true);
    }

    // Head sphere 1.2× body diameter
    translate([-15,0,10])
        sphere(12);

    // Beak – cylinder cone, 18 mm long, 3 mm base tapering to 1.5 mm
    translate([-30,0,10])
        rotate([0,-10,0])
        cylinder(h1=0, r1=1.5, h2=18, r2=3);

    // Crest – three small spheres staggered
    for(i=[-1:1])
        translate([-22, i*3, 20])
            sphere(4);

    // Eye indents (printable recesses)
    translate([-22, 6, 10])
        sphere(2.5);
    translate([-22,-6, 10])
        sphere(2.5);

    // Tail – elongated wedge
    translate([20,0,-5])
        rotate([0,0,20])
        scale([2.5,1,0.4])
        cube([8,6,10], center=true);

    // Wings – mirrored pairs
    module wing() {
        difference() {
            hull() {
                translate([0,0,0]) sphere(5);
                translate([10,0,5]) sphere(4);
            }
            // Feather grooves
            for(i=[1:3])
                translate([i*3,0,0])
                    rotate([0,90,0])
                    cylinder(h=12, r=1.2);
        }
    }
    translate([-5,10,5]) rotate([0,0,30]) wing();
    mirror([0,1,0]) translate([-5,10,5]) rotate([0,0,30]) wing();

    // Feet – two toes forward, one back for perch
    module foot() {
        union() {
            cylinder(h=6, r=2);
            translate([0,2,-3]) rotate([90,0,0]) cylinder(h=5, r=1.2);
            translate([0,-2,-3]) rotate([90,0,0]) cylinder(h=5, r=1.2);
            translate([0,0,-6]) rotate([90,0,0]) cylinder(h=4, r=1.2);
        }
    }
    translate([2, 8,-12]) foot();
    translate([2,-8,-12]) foot();
}

// Center model on build plate
translate([0,0,15]) woodpecker();

