// Wren in realistic style - medium complexity
// Approx 80mm length, 3D printable

$fn = 32;  // Smooth curves

// Main module
module wren() {
    union() {
        // Body
        body();

        // Head
        head();

        // Wings
        wing_left();
        wing_right();

        // Tail
        tail();

        // Legs and feet
        legs();
    }
}

// Body - oval shape
module body() {
    translate([0, 0, 15])
    scale([1.2, 1, 1.5])
    sphere(15);
}

// Head - slightly smaller sphere
module head() {
    translate([-20, 0, 30])
    sphere(10);

    // Beak
    translate([-30, 0, 30])
    rotate([0, 0, -10])
    scale([1.5, 0.3, 0.3])
    cylinder(h=12, r=3);

    // Eye
    translate([-25, 6, 32])
    sphere(2);
}

// Wing modules
module wing_left() {
    translate([5, -5, 25])
    rotate([0, 0, 20])
    scale([0.8, 0.2, 2])
    cylinder(h=20, r=8);

    // Wing detail
    for(i = [0:2]) {
        translate([5, -6, 25 + i*6])
        rotate([0, 0, 20])
        scale([0.6, 0.1, 0.3])
        cylinder(h=15, r=6);
    }
}

module wing_right() {
    mirror([0, 1, 0])
    wing_left();
}

// Tail - fan shape
module tail() {
    translate([15, 0, 15])
    rotate([0, 0, 20])
    scale([1.5, 0.3, 1])
    cylinder(h=25, r=10);

    // Tail feathers
    for(i = [-1:1]) {
        translate([20, i*3, 20])
        rotate([0, i*5, 20])
        scale([0.3, 0.1, 1.2])
        cylinder(h=20, r=8);
    }
}

// Legs and feet
module legs() {
    // Left leg
    translate([-5, -5, 5])
    cylinder(h=15, r=2);

    // Left foot
    translate([-5, -5, 0])
    rotate([90, 0, 0])
    scale([1, 0.5, 0.5])
    cylinder(h=10, r=3);

    // Toes
    for(i = [-1:1]) {
        translate([-5 + i*3, -5, 0])
        rotate([90, 0, i*20])
        scale([0.3, 0.3, 1])
        cylinder(h=8, r=1.5);
    }

    // Right leg (mirrored)
    mirror([0, 1, 0]) {
        translate([-5, -5, 5])
        cylinder(h=15, r=2);

        translate([-5, -5, 0])
        rotate([90, 0, 0])
        scale([1, 0.5, 0.5])
        cylinder(h=10, r=3);

        for(i = [-1:1]) {
            translate([-5 + i*3, -5, 0])
            rotate([90, 0, i*20])
            scale([0.3, 0.3, 1])
            cylinder(h=8, r=1.5);
        }
    }
}

// Generate the wren
wren();

